INSERT INTO it_control (id, name_, description, long_description, parent_id, label, type, level)
VALUES
    (0, 'ITSM-Maßnahmen', 'Maßnahmen des IT-Sicherheitsmanagements', 'ITSM-Maßnahmen umfassen strategische und operative Schritte zur Verbesserung der IT-Sicherheit', NULL, '', NULL, NULL),
    (1, 'Software', 'ITSM-Maßnahmen im Bereich Software', '', 0, '', NULL, NULL),
    (2, 'Hardware', 'ITSM-Maßnahmen im Bereich Hardware', '', 0, 'SYS', NULL, NULL),
    (3, 'Organisation', 'ITSM-Maßnahmen im Bereich Organisation und Personal', '', 0, 'ORP', NULL, NULL),
    (4, 'Betriebssysteme', 'Software zur Verwaltung von Hardware und Anwendungen', '', 1, '', NULL, NULL),
    (5, 'Anwendungssoftware', 'Software zur Unterstützung von Aufgaben', '', 1, 'APP', NULL, NULL),
    (6, 'IT-Sicherheits-Software', 'Schutz vor Schadprogrammen', '', 1, 'OPS.1.1.4', NULL, NULL),
    (7, 'Cloud-Software', 'In der Cloud betriebene Software', '', 1, 'OPS.2.2', NULL, NULL),
    (8, 'Computer', 'Nutzerbezogene IT-Systeme ohne Serverfunktion', '', 2, 'SYS.2', NULL, NULL),
    (9, 'Mobile Geräte', 'Mobile Geräte für flexible Nutzung', '', 2, 'SYS.3', NULL, NULL),
    (10, 'Netzwerk', 'Netzwerk, WLAN, VPN und deren Nutzung', '', 2, 'NET', NULL, NULL),
    (11, 'Management', 'ITSM-Maßnahmen mit Bezug zum Management', '', 3, 'ORP.1', NULL, NULL),
    (12, 'Personal', 'ITSM-Maßnahmen mit Bezug zum Personal', '', 3, 'ORP.2', NULL, NULL),
    (13, 'Schulungen', 'Sensibilisierung und Schulung zur Informationssicherheit', '', 3, 'ORP.3', NULL, NULL),
    (14, 'Sensibilisierung der Leitung', 'Sensibilisierung der Institutionsleitung für Informationssicherheit', 'Die Sensibilisierung der Institutionsleitung ist essenziell, um ein Bewusstsein für Informationssicherheit auf höchster Ebene zu schaffen. Dadurch wird sichergestellt, dass Sicherheitsmaßnahmen strategisch unterstützt und effektiv umgesetzt werden können.', 13, 'ORP.3.A1', 'PREVENTIVE', 'MUST'),
    (15, 'Einweisung des Personals', 'Einweisung des Personals in den sicheren Umgang mit IT', 'Die Schulung aller Mitarbeitenden im sicheren Umgang mit IT-Systemen stellt sicher, dass sie Risiken minimieren und institutionelle Sicherheitsrichtlinien einhalten können. Verbindliche Vorgaben schaffen Klarheit und fördern die Einhaltung.', 13, 'ORP.3.A3', 'PREVENTIVE', 'MUST'),
    (16, 'Schulungsplanung', 'Konzeption und Planung eines Sensibilisierungs- und Schulungsprogramms zur Informationssicherheit', 'Ein zielgruppenorientiertes Schulungsprogramm zur Informationssicherheit ist wichtig, um Mitarbeitenden die notwendigen Fähigkeiten und Kenntnisse zu vermitteln. Regelmäßige Überarbeitung stellt sicher, dass Schulungsinhalte aktuell und relevant bleiben.', 13, 'ORP.3.A4', 'PREVENTIVE', 'SHOULD'),
    (17, 'Schulungsdurchführung', 'Durchführung von Sensibilisierungen und Schulungen zur Informationssicherheit', 'Zielgerichtete Schulungen für Mitarbeitende sind notwendig, um ihre spezifischen Aufgaben und Verantwortlichkeiten in der Informationssicherheit zu stärken. Dies fördert eine sicherheitsbewusste Unternehmenskultur.', 13, 'ORP.3.A6', 'PREVENTIVE', 'SHOULD'),
    (18, 'IT-Grundschutz-Schulung', 'Schulung zur Vorgehensweise nach IT-Grundschutz', 'Die Qualifikation von Informationssicherheitsbeauftragten im IT-Grundschutz gewährleistet eine fundierte Umsetzung von Sicherheitsmaßnahmen. Praxisorientierte Schulungen stärken ihre Kompetenz und Handlungssicherheit.', 13, 'ORP.3.A7', 'PREVENTIVE', 'SHOULD'),
    (19, 'Auswertung des Lernerfolgs', 'Messung und Auswertung des Lernerfolgs', 'Die Messung und Auswertung der Lernerfolge in der Informationssicherheit stellt sicher, dass Schulungsziele erreicht werden. Verbesserungen in der Schulungsqualität tragen zur langfristigen Stärkung der Sicherheitskultur bei.', 13, 'ORP.3.A8', 'PREVENTIVE', 'SHOULD'),
    (20, 'Spezielle Schulungen', 'Spezielle Schulung von exponierten Personen und Institutionen', 'Vertiefende Schulungen für exponierte Personen minimieren spezifische Risiken und stärken ihre Fähigkeit, Bedrohungen zu erkennen und zu vermeiden. Dies trägt wesentlich zum Schutz der gesamten Institution bei.', 13, 'ORP.3.A9', 'PREVENTIVE', 'SHOULD'),
    (21, 'Geregeltes Onboarding', 'Geregelte Einarbeitung neuer Mitarbeitender', 'Eine strukturierte Einarbeitung neuer Mitarbeitender legt die Grundlage für die Einhaltung von Sicherheitsstandards. Durch klare Verantwortlichkeiten und unterstützende Maßnahmen wird der Integrationsprozess erleichtert.', 12, 'ORP.2.A1', 'PREVENTIVE', 'MUST'),
    (22, 'Geregeltes Offboarding', 'Geregelte Verfahrensweise beim Weggang von Mitarbeitenden', 'Ein geordneter Übergabeprozess beim Ausscheiden von Mitarbeitenden gewährleistet die Kontinuität sicherheitsrelevanter Prozesse. Gleichzeitig werden potenzielle Risiken wie Datenverluste oder Interessenkonflikte minimiert.', 12, 'ORP.2.A2', 'PREVENTIVE', 'MUST'),
    (23, 'Vertretungsregelungen', 'Festlegung von Vertretungsregelungen', 'Eine klare Vertretungsregelung im laufenden Betrieb ist essenziell, um die Kontinuität wesentlicher Geschäftsprozesse sicherzustellen. Kompetente Vertretungen sorgen dafür, dass Aufgaben auch in Abwesenheit der verantwortlichen Personen reibungslos erfüllt werden.', 12, 'ORP.2.A3', 'PREVENTIVE', 'MUST'),
    (24, 'Regelungen für Fremdpersonal', 'Festlegung von Regelungen für den Einsatz von Fremdpersonal', 'Der Umgang mit externem Personal erfordert die gleichen hohen Sicherheitsstandards wie bei eigenem Personal. Sorgfältige Einweisung und klare Vorgaben gewährleisten, dass Sicherheitsrisiken minimiert werden.', 12, 'ORP.2.A4', 'PREVENTIVE', 'MUST'),
    (25, 'Vertraulichkeit bei Fremdpersonal', 'Vertraulichkeitsvereinbarungen für den Einsatz von Fremdpersonal', 'Vertraulichkeitsvereinbarungen schützen sensible Informationen vor unbefugtem Zugriff durch externe Personen. Sie stellen sicher, dass Sicherheitsvorgaben klar kommuniziert und eingehalten werden.', 12, 'ORP.2.A5', 'PREVENTIVE', 'MUST'),
    (26, 'Zuständigkeiten von Mitarbeitenden', 'Aufgaben und Zuständigkeiten von Mitarbeitenden', 'Mitarbeitende sind verpflichtet, gesetzliche und interne Vorschriften einzuhalten, um die institutionelle Sicherheit zu gewährleisten. Bewusstsein für diese Regelungen stärkt die Einhaltung und reduziert Risiken.', 12, 'ORP.2.A14', 'PREVENTIVE', 'MUST'),
    (27, 'Qualifikation des Personals', 'Qualifikation des Personals', 'Regelmäßige Schulungen und Weiterbildungen der Mitarbeitenden gewährleisten aktuelles Wissen und qualifizierte Besetzung von Stellen. Dies trägt zur Effizienz und Sicherheit der Organisation bei.', 12, 'ORP.2.A15', 'PREVENTIVE', 'MUST'),
    (28, 'Überprüfung Vertrauenswürdigkeit', 'Überprüfung der Vertrauenswürdigkeit von Mitarbeitenden', 'Die Überprüfung der Vertrauenswürdigkeit neuer Mitarbeitender schützt vor potenziellen Sicherheitsrisiken. Gründliche Personalauswahlverfahren stellen sicher, dass nur geeignete Personen eingestellt werden.', 12, 'ORP.2.A7', 'PREVENTIVE', 'SHOULD'),
    (29, 'Sicherheitsüberprüfung', 'Sicherheitsüberprüfung', 'In sicherheitskritischen Bereichen erhöht eine zusätzliche Überprüfung der Mitarbeitenden das Schutzniveau. Dies gewährleistet, dass sensible Informationen nur von vertrauenswürdigen Personen behandelt werden.', 12, 'ORP.2.A13', 'PREVENTIVE', 'SHOULD'),
    (30, 'Definition der Verantwortlichkeiten', 'Festlegung von Verantwortlichkeiten und Regelungen', 'Klar definierte und getrennte Aufgaben und Verantwortlichkeiten fördern Transparenz und reduzieren Risiken. Regelmäßige Anpassungen stellen sicher, dass die Organisationsstruktur den aktuellen Anforderungen entspricht.', 11, 'ORP.1.A1', 'PREVENTIVE', 'MUST'),
    (31, 'Zuweisung der Zuständigkeiten', 'Zuweisung der Zuständigkeiten', 'Die klare Zuordnung von Verantwortlichkeiten für alle Prozesse, Systeme und Einrichtungen schafft Transparenz. Informierte Mitarbeitende stärken die Sicherheitskultur und fördern die Einhaltung.', 11, 'ORP.1.A2', 'PREVENTIVE', 'MUST'),
    (32, 'Beaufsichtigung', 'Beaufsichtigung oder Begleitung von Fremdpersonen', 'Institutionsfremde Personen werden durch Begleitung und Beaufsichtigung vor unbefugtem Zugriff auf sensible Bereiche geschützt. Diese Maßnahmen minimieren Sicherheitsrisiken in der Institution.', 11, 'ORP.1.A3', 'PREVENTIVE', 'MUST'),
    (33, 'Funktionstrennung', 'Funktionstrennung zwischen unvereinbaren Aufgaben', 'Eine funktionale Trennung von Verantwortlichkeiten verhindert Interessenkonflikte und erhöht die Sicherheit. Die klare Dokumentation von Aufgaben und Vertretungsregelungen sorgt für Transparenz.', 11, 'ORP.1.A4', 'PREVENTIVE', 'MUST'),
    (34, 'Ansprechperson', 'Ansprechperson zu Informationssicherheitsfragen', 'Benannte Ansprechpersonen für Sicherheitsfragen fördern die Kompetenz und den Austausch innerhalb der Institution. Leicht zugängliche Informationen stärken die Sicherheitskultur.', 11, 'ORP.1.A15', 'PREVENTIVE', 'MUST'),
    (35, 'Betriebsmittel- & Geräteverwaltung', 'Betriebsmittel- und Geräteverwaltung', 'Ausreichende Geräte und Betriebsmittel sowie deren ordnungsgemäße Verwaltung gewährleisten die Einhaltung von Sicherheitsanforderungen. Geeignete Lösch- und Entsorgungsverfahren minimieren Risiken.', 11, 'ORP.1.A8', 'PREVENTIVE', 'SHOULD'),
    (36, 'Sicherheit bei Umzügen', 'Sicherheit bei Umzügen', 'Frühzeitige Planung und Kommunikation von Sicherheitsrichtlinien bei Umzügen gewährleisten Schutz während aller Phasen. Die Kontrolle des Umzugsguts schützt vor Verlusten und Manipulation.', 11, 'ORP.1.A13', 'PREVENTIVE', 'SHOULD'),
    (37, 'Richtlinie zur sicheren IT-Nutzung', 'Richtlinie zur sicheren IT-Nutzung', 'Transparente Richtlinien zur IT-Nutzung fördern die Einhaltung von Sicherheitsmaßnahmen. Regelmäßige Bestätigung und Verfügbarkeit der Richtlinien stärken das Sicherheitsbewusstsein.', 11, 'ORP.1.A16', 'PREVENTIVE', 'SHOULD'),
    (38, 'Mitführverbot von Mobiltelefonen', 'Mitführverbot von Mobiltelefonen', 'Verzicht auf Mobiltelefone bei vertraulichen Besprechungen verhindert das Abhören sensibler Informationen. Überwachungsmechanismen erhöhen die Sicherheit bei Bedarf.', 11, 'ORP.1.A17', 'PREVENTIVE', 'SHOULD'),
    (39, 'Laptop', 'Computer die mobil genutzt werden können', '', 9, 'SYS.3.1', NULL, NULL),
    (40, 'Sichere mobile Nutzung von Laptops', 'Regelungen zur mobilen Nutzung von Laptops', 'Die mobile Nutzung von Laptops erfordert klare Regelungen zu Sicherheit und Zugriffsberechtigungen. Mitarbeitende müssen entsprechend geschult und über Sicherheitsmaßnahmen informiert werden.', 39, 'SYS.3.1.A1', 'PREVENTIVE', 'MUST'),
    (41, 'Einsatz von Firewalls', 'Einsatz von Personal Firewalls', 'Die Verwendung einer Personal Firewall auf Laptops erhöht den Schutz außerhalb des institutionellen Netzwerks. Regelmäßige Tests und restriktive Filterregeln gewährleisten die Sicherheit.', 39, 'SYS.3.1.A3', 'PREVENTIVE', 'MUST'),
    (42, 'Sicherer Fernzugriff', 'Sicherer Fernzugriff mit Laptops', 'Der Zugriff auf das interne Netz der Institution aus öffentlichen Netzwerken erfordert einen sicheren Kommunikationskanal. Diese Maßnahme schützt sensible Informationen vor unbefugtem Zugriff.', 39, 'SYS.3.1.A9', 'PREVENTIVE', 'MUST'),
    (43, 'Nutzungssrichtlinien', 'Sicherheitsrichtlinien für Laptops', 'Eine Sicherheitsrichtlinie für Laptops definiert klare Vorgaben zur Nutzung und sensibilisiert für potenzielle Risiken. Dadurch wird der Schutz sensibler Daten gestärkt.', 39, 'SYS.3.1.A6', 'PREVENTIVE', 'SHOULD'),
    (44, 'Geregelte Laptopübergabe/-rücknahme', 'Geregelte Übergabe und Rücknahme eines Laptops', 'Der sichere Umgang mit gemeinsam genutzten Laptops erfordert klare Regelungen zur Übergabe und Rücknahme. Schutzmaßnahmen wie Datenlöschung und Schadsoftware-Checks minimieren Risiken.', 39, 'SYS.3.1.A7', 'PREVENTIVE', 'SHOULD'),
    (45, 'Sicherer Anschluss an Datennetze', 'Sicherer Anschluss von Laptops an Datennetze', 'Regelungen für die sichere Netzwerkanbindung von Laptops gewährleisten den Schutz interner Daten. Nur zugelassene Geräte dürfen auf das institutionelle Netzwerk zugreifen.', 39, 'SYS.3.1.A8', 'PREVENTIVE', 'SHOULD'),
    (46, 'Abgleich der Datenbestände', 'Abgleich der Datenbestände von Laptops', 'Die Integration von Laptop-Daten in den Informationsverbund der Institution muss klar geregelt sein. Protokollierte Synchronisationsvorgänge und Anweisungen für Benutzende sorgen für Sicherheit.', 39, 'SYS.3.1.A10', 'PREVENTIVE', 'SHOULD'),
    (47, 'Sicherstellen der Energieversorgung', 'Sicherstellung der Energieversorgung von Laptops', 'Die Energieversorgung von Laptops im mobilen Einsatz muss optimal sichergestellt werden. Ersatzakkus und klare Anweisungen fördern die Betriebsfähigkeit und Sicherheit.', 39, 'SYS.3.1.A11', 'PREVENTIVE', 'SHOULD'),
    (48, 'Verlustrichtlinien', 'Verlustmeldung für Laptops', 'Der Verlust oder Diebstahl von Laptops erfordert klare Meldewege und Sicherheitsmaßnahmen. Eine vollständige Neuinstallation verhindert potenzielle Manipulationen.', 39, 'SYS.3.1.A12', 'PREVENTIVE', 'SHOULD'),
    (49, 'Datenverschlüsselung', 'Verschlüsselung von Laptops', 'Verschlüsselte Datenträger in Laptops schützen sensible Informationen vor unbefugtem Zugriff. Diese Maßnahme erhöht die Datensicherheit sowohl intern als auch extern.', 39, 'SYS.3.1.A13', 'PREVENTIVE', 'SHOULD'),
    (50, 'Sichere Aufbewahrung', 'Geeignete Aufbewahrung von Laptops', 'Sichere Aufbewahrung von Laptops innerhalb und außerhalb der Institution schützt vor Diebstahl. Mitarbeitende müssen auf die Einhaltung von Schutzmaßnahmen hingewiesen werden.', 39, 'SYS.3.1.A14', 'PREVENTIVE', 'SHOULD'),
    (51, 'Beschaffungsprozesse', 'Geeignete Auswahl von Laptops', 'Vor der Beschaffung von Laptops müssen Anforderungen analysiert und Geräte bewertet werden. Die Abstimmung mit dem IT-Betrieb gewährleistet eine geeignete Auswahl.', 39, 'SYS.3.1.A15', 'PREVENTIVE', 'SHOULD'),
    (52, 'Zentrale Administration/Verwaltung', 'Zentrale Administration und Verwaltung von Laptops', 'Zentrale Verwaltung und Administration von Laptops sorgen für einheitliche Sicherheitsstandards. Unterstützende Tools für alle Betriebssysteme fördern effizientes Management.', 39, 'SYS.3.1.A16', 'PREVENTIVE', 'SHOULD'),
    (53, 'Sichere Lagerung', 'Sammelaufbewahrung von Laptops', 'Nicht benutzte Laptops müssen in sicheren Räumen gelagert werden, die hohen Standards entsprechen. Dies schützt die Geräte vor unbefugtem Zugriff oder Beschädigung.', 39, 'SYS.3.1.A17', 'PREVENTIVE', 'SHOULD'),
    (54, 'Diebstahlsicherungen', 'Einsatz von Diebstahl-Sicherungen', 'Diebstahlsicherungen für Laptops müssen sorgfältig ausgewählt werden. Mechanische Sicherungen mit hochwertigen Schlössern bieten zusätzlichen Schutz.', 39, 'SYS.3.1.A18', 'PREVENTIVE', 'SHOULD'),
    (55, 'Desktop-Computer', 'Allgemeiner Client mit beliebigem Betriebssystem', '', 8, 'SYS.2.1', NULL, NULL),
    (56, 'Sichere Authentisierung', 'Sichere Authentisierung von Benutzenden', 'Die Nutzung von Clients erfordert Authentifizierung und Bildschirmsperren, um unbefugten Zugriff zu verhindern. Automatische Sicherheitsmaßnahmen schützen die IT-Infrastruktur.', 55, 'SYS.2.1.A1', 'PREVENTIVE', 'MUST'),
    (57, 'Aktivieren von Autoupdate', 'Aktivieren von Autoupdate-Mechanismen', 'Automatische Updates müssen aktiviert werden, um Sicherheitslücken zu schließen. Tägliche Update-Prüfungen und Installationen fördern den Schutz der Systeme.', 55, 'SYS.2.1.A3', 'PREVENTIVE', 'MUST'),
    (58, 'Einsatz von Antivirus-Software', 'Einsatz von Schutzprogrammen gegen Schadsoftware', 'Schutzprogramme gegen Schadsoftware müssen auf Clients eingesetzt und korrekt konfiguriert werden. Regelmäßige Prüfungen des Datenbestands erhöhen die Sicherheit.', 55, 'SYS.2.1.A6', 'PREVENTIVE', 'MUST'),
    (59, 'Sicherer Bootvorgang', 'Absicherung des Bootvorgangs', 'Der Bootvorgang von IT-Systemen muss gegen Manipulation geschützt werden. Nur administrierende Personen dürfen die Konfiguration ändern oder alternative Bootmedien verwenden.', 55, 'SYS.2.1.A8', 'PREVENTIVE', 'MUST'),
    (60, 'Sichere Online-Dienste', 'Nutzung von Cloud- und Online-Funktionen', 'Cloud- und Online-Funktionen von Betriebssystemen dürfen nur im notwendigen Umfang genutzt werden. Restriktive Konfigurationen und regelmäßige Überprüfungen gewährleisten Datenschutz und Sicherheit.', 55, 'SYS.2.1.A42', 'PREVENTIVE', 'MUST'),
    (61, 'Setzen einer Sicherheitsrichtlinie', 'Festlegung einer Sicherheitsrichtlinie für Clients', 'Allgemeine Sicherheitsrichtlinien für Clients müssen konkretisiert und regelmäßig überprüft werden. Die Ergebnisse sollten dokumentiert und Benutzenden bekannt gemacht werden.', 55, 'SYS.2.1.A9', 'PREVENTIVE', 'SHOULD'),
    (62, 'Planung des Einsatzes', 'Planung des Einsatzes von Clients', 'Die Planung für den Einsatz von Clients sollte sowohl IT-sicherheitsrelevante als auch betriebliche Aspekte berücksichtigen. Entscheidungen müssen nachvollziehbar dokumentiert werden.', 55, 'SYS.2.1.A10', 'PREVENTIVE', 'SHOULD'),
    (63, 'Beschaffung von Geräten', 'Beschaffung von Clients', 'Vor der Beschaffung von Clients sollte eine Anforderungsliste erstellt werden. Zu beschaffende Produkte müssen Sicherheitsanforderungen erfüllen und langfristige Updates unterstützen.', 55, 'SYS.2.1.A11', 'PREVENTIVE', 'SHOULD'),
    (64, 'Mobiltelefon', 'Mobile Telefone die keine Smartphones sind', '', 9, 'SYS.3.3', NULL, NULL),
    (65, 'Smartphone und Tablets', 'Smartphone und Tablets', '', 9, 'SYS.3.2.1', NULL, NULL),
    (66, 'Richtlinien für Handynutzung', 'Sicherheitsrichtlinien und Regelungen für die Nutzung von Mobiltelefonen', 'Eine Sicherheitsrichtlinie zur Nutzung von Mobiltelefonen muss erstellt und regelmäßig überprüft werden. Mitarbeitende sollten geschult und auf Sicherheitsmaßnahmen hingewiesen werden.', 64, 'SYS.3.3.A1', 'PREVENTIVE', 'MUST'),
    (67, 'Sperrung bei Handyverlust', 'Sperrmaßnahmen bei Verlust eines Mobiltelefons', 'Bei Verlust eines Mobiltelefons müssen die SIM-Karte gesperrt und Schutzmechanismen wie Fernlöschung genutzt werden. Alle relevanten Informationen sollten griffbereit sein.', 64, 'SYS.3.3.A2', 'PREVENTIVE', 'MUST'),
    (68, 'Sensibilisierung und Schulung', 'Sensibilisierung und Schulung der Mitarbeitenden im Umgang mit Mobiltelefonen', 'Mitarbeitende müssen für die Gefährdungen durch Mobiltelefone sensibilisiert und in Sicherheitsfunktionen eingewiesen werden. Sichere Aufbewahrung und Sperrprozesse müssen bekannt sein.', 64, 'SYS.3.3.A3', 'PREVENTIVE', 'MUST'),
    (69, 'Aussonderung und Entsorgung', 'Aussonderung und ordnungsgemäße Entsorgung von Mobiltelefonen und darin verwendeter Speicherkarten', 'Mobiltelefone müssen vor der Entsorgung zurückgesetzt und sicher gelöscht werden. Gesammelte Geräte müssen bis zur Entsorgung vor unbefugtem Zugriff geschützt werden.', 64, 'SYS.3.3.A4', 'PREVENTIVE', 'MUST'),
    (70, 'Nutzung von Sicherheitsfeatures', 'Nutzung der Sicherheitsmechanismen von Mobiltelefonen', 'Verfügbare Sicherheitsmechanismen sollten auf Mobiltelefonen genutzt und vorkonfiguriert werden. Geräte sollten durch SIM-Lock und Gerätesperrcodes geschützt werden.', 64, 'SYS.3.3.A5', 'PREVENTIVE', 'SHOULD'),
    (71, 'Regelungen für Updates', 'Updates von Mobiltelefonen', 'Softwareupdates für Mobiltelefone müssen regelmäßig geprüft und verwaltet werden. Klare Regelungen für Installation und Informationsweitergabe sollten festgelegt werden.', 64, 'SYS.3.3.A6', 'PREVENTIVE', 'SHOULD'),
    (72, 'Beschaffung von Mobiltelefonen', 'Beschaffung von Mobiltelefonen', 'Anforderungskriterien für Mobiltelefone müssen vor der Beschaffung definiert werden. Die Auswahl sollte auf langfristigen Sicherheitsupdates und Verfügbarkeit von Ersatzteilen basieren.', 64, 'SYS.3.3.A7', 'PREVENTIVE', 'SHOULD'),
    (73, 'Nutzung drahtloser Schnittstellen', 'Nutzung drahtloser Schnittstellen von Mobiltelefonen', 'Unbenutzte drahtlose Schnittstellen wie WLAN oder Bluetooth sollten deaktiviert werden. Dies reduziert Angriffsvektoren und schützt sensible Daten.', 64, 'SYS.3.3.A8', 'PREVENTIVE', 'SHOULD'),
    (74, 'Sichere Datenübertragung', 'Sichere Datenübertragung über Mobiltelefone', 'Es sollten Regelungen für Datenübertragung über Mobiltelefone definiert werden. Schnittstellen und Verschlüsselungsmethoden müssen klar festgelegt sein.', 64, 'SYS.3.3.A10', 'PREVENTIVE', 'SHOULD'),
    (75, 'Ausfallvorsorge bei Mobiltelefonen', 'Ausfallvorsorge bei Mobiltelefonen', 'Daten auf Mobiltelefonen sollten regelmäßig gesichert und vor Reparaturen gelöscht werden. Ersatzgeräte müssen verfügbar sein, um Ausfallzeiten zu minimieren.', 64, 'SYS.3.3.A11', 'PREVENTIVE', 'SHOULD'),
    (76, 'Einrichtung von Mobiltelefon-Pools', 'Einrichtung eines Mobiltelefon-Pools', 'Ein Pool-System für dienstliche Mobiltelefone sollte eingerichtet werden. Ausgabe, Rücknahme und Rücksetzungen auf Werkseinstellungen sollten dokumentiert werden.', 64, 'SYS.3.3.A12', 'PREVENTIVE', 'SHOULD'),
    (77, 'Gesicherte Energieversorgung', 'Sicherstellung der Energieversorgung von Mobiltelefonen', 'Maßnahmen zur Sicherstellung der Energieversorgung von Mobiltelefonen, wie Ersatzakkus oder Powerbanks, sollten getroffen werden. Dies gewährleistet dauerhafte Verfügbarkeit.', 64, 'SYS.3.3.A9', 'PREVENTIVE', 'SHOULD'),
    (78, 'Schutz vor Bewegungsprofilen', 'Schutz vor der Erstellung von Bewegungsprofilen bei der Nutzung von Mobilfunk', 'Die Auswirkungen von Bewegungsprofilen durch Mobiltelefone sollten bewertet werden. GPS- und Mobilfunk-Ortung können durch Abschaltung oder Akkuentfernung verhindert werden.', 64, 'SYS.3.3.A13', 'PREVENTIVE', 'SHOULD'),
    (79, 'Schutz vor Rufnummernermittlung', 'Schutz vor Rufnummernermittlung bei der Nutzung von Mobiltelefonen', 'Um die Zuordnung von Rufnummern zu Personen zu verhindern, sollten ausgehende Anrufe anonymisiert werden. Rufnummern sollten nicht veröffentlicht oder weitergegeben werden.', 64, 'SYS.3.3.A14', 'PREVENTIVE', 'SHOULD'),
    (80, 'Schutz vor Abhören', 'Schutz vor Abhören der Raumgespräche über Mobiltelefone', 'Mobiltelefone sollten nicht zu vertraulichen Besprechungen mitgenommen werden, um Abhörrisiken zu minimieren. Falls erforderlich, kann dies durch Detektoren überprüft werden.', 64, 'SYS.3.3.A15', 'PREVENTIVE', 'SHOULD'),
    (81, 'Richtlinie für den Einsatz', 'Festlegung einer Richtlinie für den Einsatz von Smartphones und Tablets', 'Eine Richtlinie für die Nutzung und Kontrolle von Smartphones und Tablets muss erstellt werden. Sie soll festlegen, wer auf welche Informationen zugreifen darf.', 65, 'SYS.3.2.1.A1', 'PREVENTIVE', 'MUST'),
    (82, 'Regelung für Cloud-Nutzung', 'Festlegung einer Strategie für die Cloud-Nutzung', 'Eine Strategie für die Cloud-Nutzung im Zusammenhang mit Smartphones und Tablets muss definiert werden. Sie soll Schutzmaßnahmen und Nutzungsgrenzen für Cloud-Dienste festlegen.', 65, 'SYS.3.2.1.A2', 'PREVENTIVE', 'MUST'),
    (83, 'Sichere Grundkonfiguration', 'Sichere Grundkonfiguration für mobile Geräte', 'Mobile Geräte müssen sicher konfiguriert werden, um ein angemessenes Schutzniveau zu gewährleisten. Überflüssige Funktionen und Schnittstellen sollten deaktiviert werden.', 65, 'SYS.3.2.1.A3', 'PREVENTIVE', 'MUST'),
    (84, 'Verwendung eines Zugriffsschutzes', 'Verwendung eines Zugriffsschutzes', 'Smartphones und Tablets müssen durch Gerätesperrcodes und Bildschirmsperren geschützt werden. Nach mehreren Entsperrfehlern sollten Sicherheitsmaßnahmen wie Zurücksetzung greifen.', 65, 'SYS.3.2.1.A4', 'PREVENTIVE', 'MUST'),
    (85, 'System- und App-Updates', 'Updates von Betriebssystem und Apps', 'Sicherheitsaktualisierungen müssen bei der Auswahl von mobilen Geräten berücksichtigt werden. Nicht mehr unterstützte Geräte und Apps müssen aussortiert werden.', 65, 'SYS.3.2.1.A5', 'PREVENTIVE', 'MUST'),
    (86, 'Datenschutzeinstellungen', 'Datenschutzeinstellungen und Berechtigungen', 'Zugriffsrechte von Apps und Betriebssystemen müssen restriktiv konfiguriert werden. Kritische Funktionen wie Kamera oder Mikrofon dürfen nur kontrolliert genutzt werden.', 65, 'SYS.3.2.1.A6', 'PREVENTIVE', 'MUST'),
    (87, 'Verhaltensregeln bei Vorfällen', 'Verhaltensregeln bei Sicherheitsvorfällen', 'Bei Verlust oder unberechtigten Änderungen an Geräten müssen Benutzende umgehend die zuständigen Stellen informieren. Dies minimiert Sicherheitsrisiken.', 65, 'SYS.3.2.1.A7', 'PREVENTIVE', 'MUST'),
    (88, 'Installation von Apps', 'Installation von Apps', 'Regeln für die Installation von Apps auf dienstlichen Geräten müssen festgelegt werden. Nur freigegebene Apps aus vertrauenswürdigen Quellen sollten zugelassen werden.', 65, 'SYS.3.2.1.A8', 'PREVENTIVE', 'MUST'),
    (89, 'Limitierung von Erweiterungen', 'Restriktive Nutzung von funktionalen Erweiterungen', 'Funktionale Erweiterungen sollten restriktiv genutzt oder vermieden werden. Sie dürfen keine sicherheitskritischen Einstellungen umgehen oder ändern.', 65, 'SYS.3.2.1.A9', 'PREVENTIVE', 'SHOULD'),
    (90, 'Richtlinie zur Benutzung', 'Richtlinie für Mitarbeitende zur Benutzung von mobilen Geräten', 'Eine verbindliche Richtlinie für die Nutzung mobiler Geräte sollte erstellt werden. Sie soll Themen wie Aufbewahrung, Verlustmeldung und Sicherheitsanforderungen abdecken.', 65, 'SYS.3.2.1.A10', 'PREVENTIVE', 'SHOULD'),
    (91, 'Verschlüsselung des Speichers', 'Verschlüsselung des Speichers', 'Der Speicher von mobilen Geräten und Speichermedien wie SD-Karten sollte verschlüsselt werden. Dies schützt sensible Daten vor unbefugtem Zugriff.', 65, 'SYS.3.2.1.A11', 'PREVENTIVE', 'SHOULD'),
    (92, 'Nicht-personalisierter Gerätenamen', 'Verwendung nicht personalisierter Gerätenamen', 'Der Gerätename sollte keine Hinweise auf die Institution oder Benutzenden enthalten, um Datenschutz zu gewährleisten.', 65, 'SYS.3.2.1.A12', 'PREVENTIVE', 'SHOULD'),
    (93, 'Screensharing und Casting', 'Regelungen zum Screensharing und Casting', 'Die Nutzung von Funktionen zur Übertragung von Bildschirminhalten sollte geregelt werden. Dabei sind organisatorische und technische Maßnahmen zu berücksichtigen.', 65, 'SYS.3.2.1.A13', 'PREVENTIVE', 'SHOULD'),
    (94, 'Kommunikationsschnittstellen', 'Deaktivierung nicht benutzter Kommunikationsschnittstellen', 'Kommunikationsschnittstellen sollten nur bei Bedarf und in geeigneten Umgebungen aktiviert werden. Ein MDM kann die zentrale Verwaltung unterstützen.', 65, 'SYS.3.2.1.A16', 'PREVENTIVE', 'SHOULD'),
    (95, 'Biometrische Authentisierung', 'Verwendung biometrischer Authentisierung', 'Biometrische Verfahren zur Authentifizierung sollten nur bei nachweislich hohem Schutz genutzt werden. Benutzende müssen über mögliche Risiken sensibilisiert werden.', 65, 'SYS.3.2.1.A18', 'PREVENTIVE', 'SHOULD'),
    (96, 'Verwendung von Sprachassistenten', 'Verwendung von Sprachassistenten', 'Sprachassistenten sollten nur bei zwingendem Bedarf genutzt werden. Eine Nutzung im gesperrten Zustand des Geräts ist nicht zu empfehlen.', 65, 'SYS.3.2.1.A19', 'PREVENTIVE', 'SHOULD'),
    (97, 'Nutzung von VPN', 'Einbindung mobiler Geräte in die interne Infrastruktur via VPN', 'Mobile Geräte sollten ausschließlich über ein VPN in die Institution eingebunden werden. Zertifikatsbasierte Authentifizierung ist Passwörtern vorzuziehen.', 65, 'SYS.3.2.1.A22', 'PREVENTIVE', 'SHOULD'),
    (98, 'Verwendung von Webfilter', 'Verwendung der Filteroption für Webseiten', 'Reputationsdienste oder Proxy-Server sollten als globale Filter für Browser hinterlegt werden. Alternativ können Allowlists und Inhaltsfilter eingesetzt werden.', 65, 'SYS.3.2.1.A28', 'PREVENTIVE', 'SHOULD'),
    (99, 'Regelung zu Mobile Payment', 'Regelung zu Mobile Payment', 'Organisationen sollten regeln, ob Mobile Payment mit dienstlichen Geräten erlaubt ist. Dies minimiert potenzielle Sicherheits- und Datenschutzrisiken.', 65, 'SYS.3.2.1.A31', 'PREVENTIVE', 'SHOULD'),
    (100, 'Nutzung von MDM-Systemen', 'Nutzung von MDM-Systemen', 'Smartphones und Tablets sollten durch ein Mobile Device Management (MDM),-System zentral verwaltet werden, um Sicherheitsrichtlinien effizient umzusetzen.', 65, 'SYS.3.2.1.A32', 'PREVENTIVE', 'SHOULD'),
    (101, 'Sicherheits-Apps', 'Auswahl und Installation von Sicherheits-Apps', 'Mobile Endgeräte sollten durch Sicherheits-Apps geschützt werden, die automatisch installiert und regelmäßig aktualisiert werden.', 65, 'SYS.3.2.1.A33', 'PREVENTIVE', 'SHOULD'),
    (102, 'Konfiguration von DNS-Server', 'Konfiguration des verwendeten DNS-Servers', 'Standard-Gateway-Einträge wie DNS-Server sollten durch sichere Alternativen ersetzt werden, um die Kommunikation zu schützen und DNS-over-HTTPS gegebenenfalls zu deaktivieren.', 65, 'SYS.3.2.1.A34', 'PREVENTIVE', 'SHOULD'),
    (103, 'Getrennte Arbeitsumgebungen', 'Nutzung von getrennten Arbeitsumgebungen', 'Bei der privaten Nutzung dienstlicher Geräte sollten getrennte Arbeitsumgebungen eingesetzt werden, damit dienstliche Daten isoliert und sicher bleiben.', 65, 'SYS.3.2.1.A25', 'PREVENTIVE', 'SHOULD'),
    (104, 'Nutzung von PIM-Containern', 'Nutzung von PIM-Containern', 'Informationen auf mobilen Endgeräten sollten in gekapselten Containern mit separater Authentisierung und Verschlüsselung geschützt werden.', 65, 'SYS.3.2.1.A26', 'PREVENTIVE', 'SHOULD'),
    (105, 'Abgesicherte Endgeräte', 'Einsatz besonders abgesicherter Endgeräte', 'Abhängig vom Schutzbedarf sollten mobile Geräte eingesetzt werden, die nach gesetzlichen Klassifizierungen zertifiziert sind.', 65, 'SYS.3.2.1.A27', 'PREVENTIVE', 'SHOULD'),
    (106, 'Verwendung von APN', 'Verwendung eines institutionsbezogenen APN', 'Institutionsbezogene Mobilfunkzugangspunkte (APN), sollten mit komplexen Authentifizierungsmechanismen konfiguriert werden, um den erlaubten Geräte-Pool einzugrenzen.', 65, 'SYS.3.2.1.A29', 'PREVENTIVE', 'SHOULD'),
    (107, 'Eingeschränkte App-Installation', 'Einschränkung der App-Installation mittels Allowlist', 'Nur freigegebene und geprüfte Apps sollten auf mobilen Endgeräten installiert werden, insbesondere bei erhöhtem Schutzbedarf.', 65, 'SYS.3.2.1.A30', 'PREVENTIVE', 'SHOULD'),
    (108, 'Verwendung einer Firewall', 'Verwendung einer Firewall', 'Smartphones und Tablets sollten mit einer aktivierten Firewall ausgestattet sein, um die Netzwerksicherheit zu gewährleisten.', 65, 'SYS.3.2.1.A35', 'PREVENTIVE', 'SHOULD'),
    (109, 'WLAN-Nutzung', 'WLAN-Nutzung', '', 10, 'NET.2.2', NULL, NULL),
    (110, 'WLAN-Betrieb', 'WLAN-Betrieb', '', 10, 'NET.2.1', NULL, NULL),
    (111, 'VPN', 'Virtual Private Network', '', 10, 'NET.3.3', NULL, NULL),
    (112, 'Erstellung einer Nutzungsrichtlinie', 'Erstellung einer Nutzungsrichtlinie für WLAN', 'Eine WLAN-Nutzungsrichtlinie muss erstellt werden, die sichere Nutzung, Datentransfer und Zugangsbeschränkungen definiert. Sie sollte regelmäßig überprüft werden.', 109, 'NET.2.2.A1', 'PREVENTIVE', 'MUST'),
    (113, 'Schulung der WLAN-Benutzenden', 'Sensibilisierung und Schulung der WLAN-Benutzenden', 'Benutzende von WLAN-Komponenten müssen geschult und sensibilisiert werden, um Sicherheitsrichtlinien einzuhalten und Gefahren zu vermeiden.', 109, 'NET.2.2.A2', 'PREVENTIVE', 'MUST'),
    (114, 'Sichere WLAN-Nutzung an Hotspots', 'Absicherung der WLAN-Nutzung an Hotspots', 'Hotspot-Nutzungen müssen sicher gestaltet werden, indem Verbindungen verschlüsselt und sensible Daten nur unter Einhaltung aller Sicherheitsmaßnahmen übertragen werden.', 109, 'NET.2.2.A3', 'PREVENTIVE', 'MUST'),
    (115, 'Regeln bei Sicherheitsvorfällen', 'Verhaltensregeln bei WLAN-Sicherheitsvorfällen', 'Bei WLAN-Sicherheitsvorfällen sollten Benutzende ihre Arbeit sichern, Zugriffe beenden und Vorfälle dokumentieren, bevor sie den IT-Betrieb informieren.', 109, 'NET.2.2.A4', 'PREVENTIVE', 'SHOULD'),
    (116, 'Strategie für WLAN-Einsatz', 'Festlegung einer Strategie für den Einsatz von WLANs', 'Die generelle Strategie für die WLAN-Nutzung muss vorab festgelegt werden, einschließlich Abdeckung, Verantwortlichkeiten und Zweck.', 110, 'NET.2.1.A1', 'PREVENTIVE', 'MUST'),
    (117, 'Geeignete WLAN-Standards', 'Auswahl eines geeigneten WLAN-Standards', 'Vor der Einführung von WLANs müssen Sicherheitsstandards evaluiert und nur sichere Authentifizierungs- und Verschlüsselungsverfahren verwendet werden.', 110, 'NET.2.1.A2', 'PREVENTIVE', 'MUST'),
    (118, 'Geeignete WLAN-Verschlüsselung', 'Auswahl geeigneter Kryptoverfahren für WLAN', 'Die Luftschnittstelle der WLAN-Kommunikation muss kryptografisch gesichert sein, wobei unsichere Verfahren wie WPA2-PSK mit schwachen Schlüsseln nicht erlaubt sind.', 110, 'NET.2.1.A3', 'PREVENTIVE', 'MUST'),
    (119, 'Geeignete Aufstellung von APs', 'Geeignete Aufstellung von Access Points', 'Access Points müssen gegen physische Manipulationen gesichert und so positioniert werden, dass Funkwellen kontrolliert bleiben.', 110, 'NET.2.1.A4', 'PREVENTIVE', 'MUST'),
    (120, 'Sichere Basis-Konfiguration der APs', 'Sichere Basis-Konfiguration der Access Points', 'Access Points dürfen nicht im Auslieferungszustand verwendet werden. Unsichere Standards müssen deaktiviert und sichere Verbindungen eingerichtet werden.', 110, 'NET.2.1.A5', 'PREVENTIVE', 'MUST'),
    (121, 'Sichere WLAN-Infrastruktur', 'Sichere Konfiguration der WLAN-Infrastruktur', 'Es muss sichergestellt sein, dass Sicherheitszonen durch WLAN-Kommunikation nicht unzulässig verbunden werden.', 110, 'NET.2.1.A6', 'PREVENTIVE', 'MUST'),
    (122, 'Aufbau eines Distribution Systems', 'Aufbau eines Distribution Systems', 'Bei kabelgebundenen Distribution-Systemen sollte entschieden werden, ob physische oder logische Trennungen wie VLANs umgesetzt werden.', 110, 'NET.2.1.A7', 'PREVENTIVE', 'MUST'),
    (123, 'Regeln bei Sicherheitsvorfällen', 'Verhaltensregeln bei WLAN-Sicherheitsvorfällen', 'Bei WLAN-Sicherheitsvorfällen muss der IT-Betrieb Maßnahmen wie Sperrung von Geräten und Infrastruktur sowie Zertifikatsverwaltung ergreifen.', 110, 'NET.2.1.A8', 'PREVENTIVE', 'MUST'),
    (124, 'Anbindung von WLANs an ein LAN', 'Sichere Anbindung von WLANs an ein LAN', 'Beim Übergang zwischen WLANs und kabelgebundenen Netzwerken sollten Schutzmaßnahmen wie Paketfilter verwendet werden.', 110, 'NET.2.1.A9', 'PREVENTIVE', 'SHOULD'),
    (125, 'Richtlinie für den Betrieb', 'Erstellung einer Sicherheitsrichtlinie für den Betrieb', 'Die grundlegenden Sicherheitsaspekte der WLAN-Nutzung sollten in Richtlinien konkretisiert und regelmäßig überprüft werden.', 110, 'NET.2.1.A10', 'PREVENTIVE', 'SHOULD'),
    (126, 'Geeignete WLAN-Komponenten', 'Geeignete Auswahl von WLAN-Komponenten', 'Eine Anforderungsliste sollte basierend auf der Planungsphase erstellt werden, um geeignete Produkte für WLAN-Infrastrukturen zu evaluieren.', 110, 'NET.2.1.A11', 'PREVENTIVE', 'SHOULD'),
    (127, 'WLAN-Management-Lösungen', 'Einsatz einer geeigneten WLAN-Management-Lösung', 'Eine zentrale Managementlösung sollte implementiert werden, um Anforderungen der WLAN-Strategie effizient zu erfüllen.', 110, 'NET.2.1.A12', 'PREVENTIVE', 'SHOULD'),
    (128, 'Regelmäßige Sicherheitschecks', 'Regelmäßige Sicherheitschecks in WLANs', 'WLANs sollten regelmäßig auf Sicherheitslücken geprüft werden. Ergebnisse sollten dokumentiert und Abweichungen untersucht werden.', 110, 'NET.2.1.A13', 'PREVENTIVE', 'SHOULD'),
    (129, 'Audits der WLAN-Komponenten', 'Regelmäßige Audits der WLAN-Komponenten', 'Die Konfiguration und Sicherheit der WLAN-Komponenten sollte regelmäßig geprüft werden. Manipulationsversuche an öffentlich zugänglichen Access Points sollten dokumentiert werden.', 110, 'NET.2.1.A14', 'PREVENTIVE', 'SHOULD'),
    (130, 'VPN zur Absicherung von WLANs', 'Verwendung eines VPN zur Absicherung von WLANs', 'Eine VPN-Lösung sollte implementiert werden, um die Kommunikation über WLAN-Infrastrukturen zusätzlich abzusichern. Dies ist wichtig, um Angriffe auf sensible Daten während der Übertragung zu verhindern und die Vertraulichkeit der Kommunikation zu gewährleisten.', 110, 'NET.2.1.A15', 'PREVENTIVE', 'SHOULD'),
    (131, 'Absicherung bei WLAN-LAN-Anbindung', 'Zusätzliche Absicherung bei der Anbindung von WLANs an ein LAN', 'Der Übergang zwischen WLAN- und LAN-Infrastrukturen sollte entsprechend des höheren Schutzbedarfs abgesichert werden. Dies verhindert unbefugten Zugriff und stellt sicher, dass Sicherheitsrichtlinien durchgängig umgesetzt werden.', 110, 'NET.2.1.A16', 'PREVENTIVE', 'SHOULD'),
    (132, 'Sichere Kommunikation zwischen APs', 'Absicherung der Kommunikation zwischen Access Points', 'Die Kommunikation zwischen Access Points und LAN sollte verschlüsselt erfolgen. Dies schützt vor Abhörversuchen und stellt die Integrität und Vertraulichkeit sensibler Informationen sicher.', 110, 'NET.2.1.A17', 'PREVENTIVE', 'SHOULD'),
    (133, 'Wireless Intrusion Detection', 'Einsatz von Wireless Intrusion Detection/Wireless Intrusion Prevention Systemen', 'Wireless Intrusion Detection Systeme oder Wireless Intrusion Prevention Systeme sollten eingesetzt werden, um Angriffe auf WLAN-Netzwerke frühzeitig zu erkennen und proaktiv Maßnahmen zu ergreifen. Sie bieten eine zusätzliche Sicherheitsebene, indem sie ungewöhnliche Aktivitäten im Netzwerk überwachen und melden.', 110, 'NET.2.1.A18', 'PREVENTIVE', 'SHOULD'),
    (134, 'Planung des VPN-Einsatzes', 'Planung des VPN-Einsatzes', 'Die Einführung eines VPN muss sorgfältig geplant werden, einschließlich der Festlegung von Verantwortlichkeiten und Benutzerberechtigungen. Eine durchdachte Planung stellt sicher, dass der VPN-Betrieb effizient und sicher erfolgt und Zugriffe klar dokumentiert sind.', 111, 'NET.3.3.A1', 'PREVENTIVE', 'MUST'),
    (135, 'Auswahl von VPN-Dienstleistenden', 'Auswahl von VPN-Dienstleistenden', 'VPN-Dienstleistende müssen durch Service Level Agreements (SLAs), vertraglich gebunden und regelmäßig überprüft werden. Dies gewährleistet, dass die vereinbarten Sicherheits- und Serviceanforderungen eingehalten werden.', 111, 'NET.3.3.A2', 'PREVENTIVE', 'MUST'),
    (136, 'Sichere Installation von Endgeräten', 'Sichere Installation von VPN-Endgeräten', 'Eine Appliance, die für VPN-Zugriffe verwendet wird, muss qualifiziert installiert und gewartet werden. Dies reduziert die Wahrscheinlichkeit von Fehlkonfigurationen und stellt sicher, dass Sicherheitsmechanismen ordnungsgemäß funktionieren.', 111, 'NET.3.3.A3', 'PREVENTIVE', 'MUST'),
    (137, 'Sichere Konfiguration eines VPN', 'Sichere Konfiguration eines VPN', 'Für alle VPN-Komponenten muss eine sichere Konfiguration festgelegt werden. Dies minimiert Schwachstellen und schützt vor unbefugtem Zugriff, insbesondere durch veraltete oder unsichere Einstellungen.', 111, 'NET.3.3.A4', 'PREVENTIVE', 'MUST'),
    (138, 'Sperrung obsoleter VPN-Zugänge', 'Sperrung nicht mehr benötigter VPN-Zugänge', 'Es muss regelmäßig geprüft werden, ob nur berechtigte IT-Systeme und Benutzende auf das VPN zugreifen können. Diese Überprüfung verhindert Sicherheitsvorfälle durch missbräuchliche oder nicht mehr benötigte Zugänge.', 111, 'NET.3.3.A5', 'PREVENTIVE', 'MUST'),
    (139, 'VPN-Anforderungsanalyse', 'Durchführung einer VPN-Anforderungsanalyse', 'Eine Anforderungsanalyse für VPNs sollte durchgeführt werden, um die spezifischen Einsatzszenarien und benötigten Ressourcen zu bestimmen. Dies hilft, die Lösung optimal an die Anforderungen der Institution anzupassen.', 111, 'NET.3.3.A6', 'PREVENTIVE', 'SHOULD'),
    (140, 'Planung der VPN-Realisierung', 'Planung der technischen VPN-Realisierung', 'Technische Aspekte des VPN-Einsatzes, wie Verschlüsselungsverfahren, Endpunkte und Zugangsprotokolle, sollten sorgfältig geplant werden. Diese Planung gewährleistet die Sicherheit und Effizienz des VPNs.', 111, 'NET.3.3.A7', 'PREVENTIVE', 'SHOULD'),
    (141, 'Richtlinie zur VPN-Nutzung', 'Erstellung einer Sicherheitsrichtlinie zur VPN-Nutzung', 'Eine Sicherheitsrichtlinie für die VPN-Nutzung sollte erstellt und allen Mitarbeitenden bekannt gemacht werden. Dies fördert einheitliches Verhalten und minimiert Sicherheitsrisiken durch unsachgemäße Nutzung.', 111, 'NET.3.3.A8', 'PREVENTIVE', 'SHOULD'),
    (142, 'Geeignete Auswahl von VPN-Produkten', 'Geeignete Auswahl von VPN-Produkten', 'Bei der Auswahl von VPN-Produkten sollten die Vernetzungsanforderungen der Institution berücksichtigt werden. Dadurch wird sichergestellt, dass die Produkte die notwendige Funktionalität und Sicherheit bieten.', 111, 'NET.3.3.A9', 'PREVENTIVE', 'SHOULD'),
    (143, 'Sicherer Betrieb eines VPN', 'Sicherer Betrieb eines VPN', 'Ein Betriebskonzept für VPNs sollte erstellt werden, um Qualitätskontrollen, Wartung und Autorisierung zu standardisieren. Dies gewährleistet eine langfristige Sicherheit und Verfügbarkeit des VPNs.', 111, 'NET.3.3.A10', 'PREVENTIVE', 'SHOULD'),
    (144, 'Sichere Anbindung externer Netze', 'Sichere Anbindung eines externen Netzes', 'Es sollte sichergestellt werden, dass VPN-Verbindungen nur zwischen definierten IT-Systemen und Diensten bestehen. Dies verhindert unbefugte Zugriffe und gewährleistet die Integrität der Datenübertragung.', 111, 'NET.3.3.A11', 'PREVENTIVE', 'SHOULD'),
    (145, 'Konten- und Zugriffsverwaltung', 'Konten- und Zugriffsverwaltung bei Fernzugriff-VPNs', 'Eine zentrale und konsistente Verwaltung von Benutzerkonten für Fernzugriff-VPNs sollte gewährleistet werden. Dies reduziert Komplexität und potenzielle Sicherheitslücken durch inkonsistente Benutzerkontrollen.', 111, 'NET.3.3.A12', 'PREVENTIVE', 'SHOULD'),
    (146, 'Firewall-Integration von VPN', 'Integration von VPN-Komponenten in eine Firewall', 'Die Integration von VPN-Komponenten in die Firewall verbessert die Sicherheitslage durch die Kombination von Zugangskontrolle und Schutzmechanismen. Dies verhindert unbefugte Zugriffe und Angriffspunkte.', 111, 'NET.3.3.A13', 'PREVENTIVE', 'SHOULD'),
    (147, 'Windows (Client)', 'Windows (Client)', '', 4, 'SYS.2.2.3', NULL, NULL),
    (148, 'Linux und Unix (Client)', 'Linux und Unix (Client)', '', 4, 'SYS.2.3', NULL, NULL),
    (149, 'macOS (Client)', 'macOS (Client)', '', 4, 'SYS.2.4', NULL, NULL),
    (150, 'Android', 'Android', '', 4, 'SYS.3.2.4', NULL, NULL),
    (151, 'iOS', 'iOS', '', 4, 'SYS.3.2.3', NULL, NULL),
    (152, 'Office-Produkte', 'Office-Produkte', '', 5, 'APP.1.1', NULL, NULL),
    (153, 'Mobile Anwendungen (Apps)', 'Mobile Anwendungen (Apps)', '', 5, 'APP.1.4', NULL, NULL),
    (154, 'E-Mail', 'E-Mail', '', 5, 'APP.5.3', NULL, NULL),
    (155, 'Webbrowser', 'Webbrowser', '', 5, 'APP.1.2', NULL, NULL),
    (156, 'Webanwendungen und Webservices', 'Webanwendungen und Webservices', '', 5, 'APP.3.1', NULL, NULL),
    (157, 'Schutz vor Schadprogrammen', 'Erstellung eines Konzepts für den Schutz vor Schadprogrammen', 'Ein Schutzkonzept für IT-Systeme gegen Schadprogramme, einschließlich IoT-Geräten, sollte erstellt werden. Dies sichert die Verfügbarkeit der Systeme und stellt die Einhaltung gesetzlicher Anforderungen sicher.', 6, 'OPS.1.1.4.A1', 'PREVENTIVE', 'MUST'),
    (158, 'Systemspezifische Schutzmechanismen', 'Nutzung systemspezifischer Schutzmechanismen', 'Vorhandene Schutzmechanismen in Betriebssystemen und Anwendungen sollten genutzt werden. Dies reduziert Schwachstellen und gewährleistet eine robuste Abwehr gegen Bedrohungen.', 6, 'OPS.1.1.4.A2', 'PREVENTIVE', 'MUST'),
    (159, 'Auswahl eines Virenschutzprogrammes', 'Auswahl eines Virenschutzprogrammes', 'Antivirenprogramme, die speziell für Institutionen entwickelt wurden, sollten ausgewählt und eingesetzt werden. Solche Programme bieten umfassenden Schutz und minimieren das Risiko von Schadsoftware-Angriffen.', 6, 'OPS.1.1.4.A3', 'PREVENTIVE', 'MUST'),
    (160, 'Einsatz von Virenschutzprogrammen', 'Betrieb und Konfiguration von Virenschutzprogrammen', 'Antivirenprogramme sollten optimal konfiguriert werden, um IT-Systeme effektiv vor Bedrohungen zu schützen. Dies stellt sicher, dass Benutzer keine sicherheitskritischen Änderungen vornehmen können, die das System gefährden.', 6, 'OPS.1.1.4.A5', 'PREVENTIVE', 'MUST'),
    (161, 'Update der Virenschutzprogramme', 'Regelmäßige Aktualisierung der eingesetzten Virenschutzprogramme', 'IT-Systeme müssen regelmäßig mit aktualisierten Virenschutzprogrammen versorgt werden. Dies gewährleistet den Schutz vor neuen Bedrohungen durch Schadprogramme und minimiert Sicherheitsrisiken durch veraltete Signaturen.', 6, 'OPS.1.1.4.A6', 'PREVENTIVE', 'MUST'),
    (162, 'Verpflichtung der Nutzer', 'Sensibilisierung und Verpflichtung der Benutzenden', 'Benutzende müssen über die Bedrohung durch Schadprogramme aufgeklärt und geschult werden. Dadurch wird das Risiko einer Infektion durch unsachgemäßes Verhalten reduziert und die allgemeine Sicherheitsbewusstheit erhöht.', 6, 'OPS.1.1.4.A7', 'PREVENTIVE', 'MUST'),
    (163, 'Meldung von Infektionen', 'Meldung von Infektionen mit Schadprogrammen', 'Virenschutzprogramme sollten Infektionen automatisch blockieren und zentral melden. Dies ermöglicht eine schnelle Reaktion auf Sicherheitsvorfälle und unterstützt die zuständigen Teams bei der koordinierten Problemlösung.', 6, 'OPS.1.1.4.A9', 'PREVENTIVE', 'SHOULD'),
    (164, 'Nutzung von Analyseumgebungen', 'Nutzung spezieller Analyseumgebungen', 'Verdächtige Dateien sollten in speziellen Testumgebungen analysiert werden. Dies minimiert das Risiko, dass potenziell schädliche Dateien produktive Systeme gefährden, und erlaubt eine gründliche Untersuchung in einer kontrollierten Umgebung.', 6, 'OPS.1.1.4.A10', 'PREVENTIVE', 'SHOULD'),
    (165, 'Einsatz mehrerer Scan-Engines', 'Einsatz mehrerer Scan-Engines', 'Für besonders schutzwürdige IT-Systeme sollten Virenschutzprogramme mit mehreren Scan-Engines eingesetzt werden. Dies erhöht die Wahrscheinlichkeit, Schadprogramme zu erkennen, und bietet eine stärkere Sicherheitsgarantie.', 6, 'OPS.1.1.4.A11', 'PREVENTIVE', 'SHOULD'),
    (166, 'Einsatz von Datenträgerschleusen', 'Einsatz von Datenträgerschleusen', 'Datenträger von Dritten sollten vor der Nutzung durch eine Datenträgerschleuse geprüft werden. Dies verhindert, dass externe Medien Schadprogramme in das Netzwerk einschleusen.', 6, 'OPS.1.1.4.A12', 'PREVENTIVE', 'SHOULD'),
    (167, 'Nicht vertrauenswürdige Dateien', 'Umgang mit nicht vertrauenswürdigen Dateien', 'Nicht vertrauenswürdige Dateien sollten auf isolierten IT-Systemen geöffnet werden. Dies verringert das Risiko, produktive Systeme zu infizieren, und bietet einen sicheren Raum für die Verarbeitung solcher Dateien.', 6, 'OPS.1.1.4.A13', 'PREVENTIVE', 'SHOULD'),
    (168, 'Abwehr gegen gezielte Angriffe', 'Auswahl und Einsatz von Cyber-Sicherheitsprodukten gegen gezielte Angriffe', 'Produkte mit erweitertem Schutzumfang sollten auf ihren Mehrwert geprüft werden. Dies stellt sicher, dass zusätzliche Sicherheitslösungen effektiv in bestehende IT-Infrastrukturen integriert werden und tatsächlichen Nutzen bieten.', 6, 'OPS.1.1.4.A14', 'PREVENTIVE', 'SHOULD'),
    (169, 'Strategie für die Cloud-Nutzung', 'Erstellung einer Strategie für die Cloud-Nutzung', 'Eine Cloud-Nutzungsstrategie muss erstellt werden, die Ziele, Chancen und Risiken definiert. Dies gewährleistet eine strukturierte und sichere Implementierung von Cloud-Diensten unter Berücksichtigung gesetzlicher und technischer Anforderungen.', 7, 'OPS.2.2.A1', 'PREVENTIVE', 'MUST'),
    (170, 'Richtlinie für die Cloud-Nutzung', 'Erstellung einer Sicherheitsrichtlinie für die Cloud-Nutzung', 'Auf Basis der Cloud-Strategie muss eine Sicherheitsrichtlinie für die Cloud-Nutzung entwickelt werden. Diese schützt die Vertraulichkeit, Integrität und Verfügbarkeit der Daten in der Cloud und sorgt für klare Vorgaben an die Cloud-Diensteanbietenden.', 7, 'OPS.2.2.A2', 'PREVENTIVE', 'MUST'),
    (171, 'Service-Definition', 'Service-Definition für Cloud-Dienste', 'Für jeden Cloud-Dienst muss eine Service-Definition erarbeitet werden. Dies schafft Transparenz über geplante und genutzte Dienste und definiert klar Verantwortlichkeiten und Leistungsgrenzen.', 7, 'OPS.2.2.A3', 'PREVENTIVE', 'MUST'),
    (172, 'Verantwortungsbereiche', 'Festlegung von Verantwortungsbereichen und Schnittstellen', 'Schnittstellen und Verantwortlichkeiten in der Cloud-Nutzung müssen identifiziert und dokumentiert werden. Dies gewährleistet klare Zuständigkeiten zwischen der Institution und den Cloud-Diensteanbietenden.', 7, 'OPS.2.2.A4', 'PREVENTIVE', 'MUST'),
    (173, 'Planung der sicheren Migration', 'Planung der sicheren Migration zu einem Cloud-Dienst', 'Vor der Migration zu einem Cloud-Dienst sollte ein Migrationskonzept erstellt werden. Dies minimiert Risiken während der Migration und sorgt für eine nahtlose Integration in bestehende Prozesse.', 7, 'OPS.2.2.A5', 'PREVENTIVE', 'SHOULD'),
    (174, 'Planung der sicheren Einbindung', 'Planung der sicheren Einbindung von Cloud-Diensten', 'Cloud-Dienste sollten sorgfältig geplant und in die IT-Infrastruktur eingebunden werden. Dies vermeidet technische und organisatorische Probleme und gewährleistet eine effiziente Nutzung der Cloud-Dienste.', 7, 'OPS.2.2.A6', 'PREVENTIVE', 'SHOULD'),
    (175, 'Konzept für Cloud-Nutzung', 'Erstellung eines Sicherheitskonzeptes für die Cloud-Nutzung', 'Ein Sicherheitskonzept für die Nutzung von Cloud-Diensten sollte erstellt werden. Es sorgt für die systematische Umsetzung aller identifizierten Sicherheitsanforderungen.', 7, 'OPS.2.2.A7', 'PREVENTIVE', 'SHOULD'),
    (176, 'Auswahl von Diensteanbietern', 'Sorgfältige Auswahl von Cloud-Diensteanbietenden', 'Ein detailliertes Anforderungsprofil für Cloud-Diensteanbietende sollte erstellt werden. Dies gewährleistet, dass die ausgewählten Anbieter den Anforderungen der Institution entsprechen.', 7, 'OPS.2.2.A8', 'PREVENTIVE', 'SHOULD'),
    (177, 'Vertragsgestaltung', 'Vertragsgestaltung mit den Cloud-Diensteanbietenden', 'Vertragliche Regelungen mit Cloud-Diensteanbietenden sollten dem Schutzbedarf angepasst werden. Dies schützt die sensiblen Daten der Institution und stellt klare Eskalations- und Kündigungsprozesse sicher.', 7, 'OPS.2.2.A9', 'PREVENTIVE', 'SHOULD'),
    (178, 'Sichere Migration zu Cloud-Dienst', 'Sichere Migration zu einem Cloud-Dienst', 'Die Migration zu einem Cloud-Dienst sollte auf Basis des erstellten Konzepts erfolgen. Dies gewährleistet, dass alle Sicherheits- und Notfallmaßnahmen berücksichtigt und getestet werden.', 7, 'OPS.2.2.A10', 'PREVENTIVE', 'SHOULD'),
    (179, 'Notfallkonzeptes für Cloud-Dienst', 'Erstellung eines Notfallkonzeptes für einen Cloud-Dienst', 'Für genutzte Cloud-Dienste sollte ein Notfallkonzept erstellt werden. Dieses stellt sicher, dass Zuständigkeiten klar definiert sind und die Institution auf Störungen oder Datenverluste vorbereitet ist.', 7, 'OPS.2.2.A11', 'PREVENTIVE', 'SHOULD'),
    (180, 'IT-Sicherheit bei Cloud-Nutzung', 'Aufrechterhaltung der Informationssicherheit im laufenden Cloud-Nutzungs-Betrieb', 'Alle Dokumentationen und Richtlinien zu Cloud-Diensten sollten regelmäßig aktualisiert werden. Dies gewährleistet, dass Änderungen in der Infrastruktur und neue Anforderungen zeitnah berücksichtigt werden.', 7, 'OPS.2.2.A12', 'PREVENTIVE', 'SHOULD'),
    (181, 'Nachweis über IT-Sicherheit', 'Nachweis einer ausreichenden Informationssicherheit bei der Cloud-Nutzung', 'Cloud-Diensteanbietende sollten regelmäßig nachweisen, dass Sicherheitsanforderungen erfüllt sind. Dies gibt der Institution Sicherheit, dass vereinbarte Standards eingehalten werden.', 7, 'OPS.2.2.A13', 'PREVENTIVE', 'SHOULD'),
    (182, 'Geordnete Dienstkündigung', 'Geordnete Beendigung eines Cloud-Nutzungs-Verhältnisses', 'Der Ausstieg aus Cloud-Diensten sollte so geregelt sein, dass die Geschäftstätigkeit nicht beeinträchtigt wird. Dies minimiert Risiken bei Beendigung der Geschäftsbeziehung mit Cloud-Diensteanbietenden.', 7, 'OPS.2.2.A14', 'PREVENTIVE', 'SHOULD'),
    (183, 'Sicherstellung der Portabilität', 'Sicherstellung der Portabilität von Cloud-Diensten', 'Die Portabilität von Cloud-Diensten sollte regelmäßig getestet werden. Dies sichert die Flexibilität der Institution, den Anbieter zu wechseln oder Daten zurückzuholen.', 7, 'OPS.2.2.A15', 'PREVENTIVE', 'SHOULD'),
    (184, 'Eigene Datensicherungen', 'Durchführung eigener Datensicherungen', 'Zusätzliche Datensicherungen sollten unabhängig von Cloud-Diensteanbietenden geprüft werden. Dies schützt wichtige Daten vor Verlust und erhöht die Datenverfügbarkeit.', 7, 'OPS.2.2.A16', 'PREVENTIVE', 'SHOULD'),
    (185, 'Einsatz von Verschlüsselung', 'Einsatz von Verschlüsselung bei Cloud-Nutzung', 'Verschlüsselung bei Cloud-Diensten sollte vertraglich geregelt werden. Dies garantiert, dass sensible Informationen angemessen geschützt sind, unabhängig vom genutzten Service-Modell.', 7, 'OPS.2.2.A17', 'PREVENTIVE', 'SHOULD'),
    (186, 'Einsatz von Verbunddiensten', 'Einsatz von Verbunddiensten', 'Der Einsatz von Federation Services bei Cloud-Nutzung sollte geprüft werden. Dies sorgt für sichere und effiziente Authentifizierung bei der Nutzung von Cloud-Diensten.', 7, 'OPS.2.2.A18', 'PREVENTIVE', 'SHOULD'),
    (187, 'Mitarbeiterprüfung', 'Sicherheitsüberprüfung von Mitarbeitenden', 'Die Qualifikation und Vertrauenswürdigkeit des Personals bei Cloud-Diensteanbietenden sollte überprüft werden. Dies stellt sicher, dass sensible Daten nur von geschultem und zuverlässigem Personal gehandhabt werden.', 7, 'OPS.2.2.A19', 'PREVENTIVE', 'SHOULD'),
    (188, 'Deaktivieren des Entwicklermodus', 'Deaktivieren des Entwicklermodus', 'Der Entwicklermodus auf Android-Geräten sollte deaktiviert sein. Dies minimiert die Angriffsfläche und verhindert unbefugte Änderungen an den Geräten.', 150, 'SYS.3.2.4.A2', 'PREVENTIVE', 'SHOULD'),
    (189, 'Multi-User- und Gäste-Modus', 'Einsatz des Multi-User- und Gäste-Modus', 'Die Nutzung eines Geräts durch mehrere Personen sollte klar geregelt sein. Dies sorgt für eine kontrollierte Verwendung und schützt persönliche sowie institutionelle Daten.', 150, 'SYS.3.2.4.A3', 'PREVENTIVE', 'SHOULD'),
    (190, 'Strenge Sicherheitseinstellungen', 'Erweiterte Sicherheitseinstellungen', 'Nur freigegebene Sicherheits-Apps sollten Rechte zur Geräteadministration erhalten oder sich als „Trust Agents“ eintragen lassen, und dies sollte regelmäßig überprüft werden. Dies verhindert, dass unautorisierte Anwendungen sicherheitskritische Funktionen übernehmen und schützt die Integrität der Geräte.', 150, 'SYS.3.2.4.A5', 'PREVENTIVE', 'SHOULD'),
    (191, 'Strategie für die iOS-Nutzung', 'Strategie für die iOS-Nutzung', 'iOS-basierte Geräte müssen über ein MDM verwaltet und konfiguriert werden. Dies stellt sicher, dass sie konsistent mit den Sicherheitsrichtlinien der Institution betrieben werden. Eine Strategie zur iOS-Nutzung muss vorhanden sein, um Aspekte wie Gerätauswahl und Datensicherung festzulegen. Jailbreaks müssen organisatorisch und technisch verhindert werden, um die Sicherheit und Kontrolle über die Geräte zu gewährleisten.', 151, 'SYS.3.2.3.A1', 'PREVENTIVE', 'MUST'),
    (192, 'Strategie für Cloud-Dienste', 'Planung des Einsatzes von Cloud-Diensten', 'Bevor iOS-Geräte verwendet werden, muss der Einsatz von Cloud-Services festgelegt werden. Dies ist wichtig, da diese Geräte eng mit iCloud-Diensten verbunden sind. Optionen wie der Apple Business Manager sollten geprüft werden, um eine sichere und effiziente Geräteverwaltung zu ermöglichen.', 151, 'SYS.3.2.3.A2', 'PREVENTIVE', 'MUST'),
    (193, 'Sichere Konfigurationsprofile', 'Verhinderung des unautorisierten Löschens von Konfigurationsprofilen', 'Konfigurationsprofile dürfen nicht unautorisiert gelöscht werden. Technische Maßnahmen wie der betreute Modus müssen implementiert werden, um dies zu verhindern. Sensibilisierung der Benutzenden für Sicherheitsmaßnahmen trägt zur Akzeptanz und Einhaltung der Vorgaben bei.', 151, 'SYS.3.2.3.A7', 'PREVENTIVE', 'MUST'),
    (194, 'Verwendung von Apple-IDs', 'Verwendung von Apple-IDs', 'Anonymisierte Apple-IDs sollten anstelle persönlicher IDs verwendet werden. Der Apple Business Manager kann für Volumenlizenzen und zentralisierte App-Installationen eingesetzt werden, um die Verwaltung zu erleichtern und Sicherheitsrisiken zu minimieren.', 151, 'SYS.3.2.3.A12', 'PREVENTIVE', 'SHOULD'),
    (195, 'Reduzierter Funktionsumfang', 'Verwendung der Konfigurationsoption "Einschränkungen unter iOS"', 'Nicht benötigte oder erlaubte Funktionen von iOS sollten deaktiviert werden. Eine Prüfung basierend auf Einsatzzweck und Schutzbedarf stellt sicher, dass nur notwendige Funktionen wie Sperrbildschirm oder Unified Communication aktiviert sind.', 151, 'SYS.3.2.3.A13', 'PREVENTIVE', 'SHOULD'),
    (196, 'Verwendung der iCloud-Infrastruktur', 'Verwendung der iCloud-Infrastruktur', 'Die Nutzung der iCloud-Infrastruktur für dienstliche Zwecke sollte bewertet werden. Die Einhaltung interner Richtlinien bezüglich Datenschutz und Sicherheit muss gewährleistet sein, und die Identität sollte durch Zwei-Faktor-Authentisierung abgesichert werden.', 151, 'SYS.3.2.3.A14', 'PREVENTIVE', 'SHOULD'),
    (197, 'Strategie für Continuity-Funktionen', 'Verwendung der Continuity-Funktionen', 'Die Vereinbarkeit von Continuity-Funktionen mit internen Richtlinien sollte bewertet werden. Basierend auf den Ergebnissen sollten diese Funktionen eingeschränkt werden, um Sicherheitsrisiken zu vermeiden.', 151, 'SYS.3.2.3.A15', 'PREVENTIVE', 'SHOULD'),
    (198, 'Verwendung der Gerätecode-Historie', 'Verwendung der Gerätecode-Historie', 'Im Konfigurationsprofil sollte die Anzahl eindeutiger Codes bis zur Wiederholung auf einen angemessenen Wert festgelegt sein, um die Sicherheit zu erhöhen.', 151, 'SYS.3.2.3.A17', 'PREVENTIVE', 'SHOULD'),
    (199, 'Richtlinie für Safari-Browser', 'Verwendung der Konfigurationsoption für den Browser Safari', 'Browserrichtlinien der Institution sollten für Safari technisch und organisatorisch umgesetzt werden. Dies stellt sicher, dass Sicherheitsanforderungen konsistent auf iOS-Geräten angewandt werden.', 151, 'SYS.3.2.3.A18', 'PREVENTIVE', 'SHOULD'),
    (200, 'Sichere App-Store-Konfiguration', 'Installation von Apps und Einbindung des Apple App Stores', 'Der Apple Business Manager sollte in die MDM-Infrastruktur integriert werden, um sicherzustellen, dass benötigte Apps rechtzeitig verfügbar sind. Biometrische Verfahren sollten nicht für Zahlungen im App Store verwendet werden, um Missbrauch zu verhindern.', 151, 'SYS.3.2.3.A21', 'PREVENTIVE', 'SHOULD'),
    (201, 'Konfigurationsprofillöschung', 'Verwendung der automatischen Konfigurationsprofillöschung', 'Geräte, die über einen definierten Zeitraum offline sind, sollten ihren Zugang zur Infrastruktur verlieren. Dies schützt vor Missbrauch und sorgt dafür, dass nur aktive und vertrauenswürdige Geräte verbunden bleiben.', 151, 'SYS.3.2.3.A23', 'PREVENTIVE', 'SHOULD'),
    (202, 'Konfigurationsoption für AirPrint', 'Verwendung der Konfigurationsoption für AirPrint', 'AirPrint-Drucker sollten über ein Konfigurationsprofil bereitgestellt werden, um sicherzustellen, dass Benutzende keine Informationen auf nicht vertrauenswürdigen Druckern drucken können.', 151, 'SYS.3.2.3.A25', 'PREVENTIVE', 'SHOULD'),
    (203, 'Keine Verbindung mit Host-Systemen', 'Keine Verbindung mit Host-Systemen', 'iOS-Geräte sollten nur mit dem MDM verbunden werden können, um unautorisierte Verbindungen mit anderen IT-Systemen zu verhindern.', 151, 'SYS.3.2.3.A26', 'PREVENTIVE', 'SHOULD'),
    (204, 'Strategie für Cloud-Dienste', 'Planung des Einsatzes von Cloud-Diensten unter Windows', 'Die Nutzung von Microsoft Cloud-Diensten auf Windows-Geräten muss strategisch geregelt werden, da diese Geräte eng mit diesen Diensten verzahnt sind. Dies schützt vor unbeabsichtigtem Datenverlust oder Sicherheitsrisiken.', 147, 'SYS.2.2.3.A1', 'PREVENTIVE', 'MUST'),
    (205, 'Geeignete Windows-Version', 'Auswahl und Beschaffung einer geeigneten Windows-Version', 'Die Auswahl von Windows-Versionen muss auf Schutzbedarf und Einsatzzweck basieren. Dies stellt sicher, dass Sicherheitsmaßnahmen effektiv umgesetzt werden können.', 147, 'SYS.2.2.3.A2', 'PREVENTIVE', 'MUST'),
    (206, 'Datenschutzeinstellungen', 'Telemetrie und Datenschutzeinstellungen unter Windows', 'Das Telemetrie-Level 0 (Security), muss auf Windows-Geräten konfiguriert werden, um die Übertragung von Diagnosedaten an Microsoft stark zu reduzieren.', 147, 'SYS.2.2.3.A4', 'PREVENTIVE', 'MUST'),
    (207, 'Schutz vor Schadsoftware', 'Schutz vor Schadsoftware unter Windows', 'Spezialisierte Schutzkomponenten gegen Schadsoftware müssen auf Windows-Clients eingesetzt werden, wenn keine gleichwertigen Schutzmaßnahmen vorhanden sind.', 147, 'SYS.2.2.3.A5', 'PREVENTIVE', 'MUST'),
    (208, 'Integration von Online-Konten', 'Integration von Online-Konten in das Betriebssystem', 'Anmeldungen am System sollten nur mit einem Verzeichnisdienstkonto erfolgen. Online-Konten sind zu vermeiden, um personenbezogene Daten vor Übertragung an Dritte zu schützen.', 147, 'SYS.2.2.3.A6', 'PREVENTIVE', 'MUST'),
    (209, 'Sichere zentrale Authentisierung', 'Sichere zentrale Authentisierung in Windows-Netzen', 'Kerberos sollte für zentrale Authentisierung verwendet werden. Ältere Protokolle wie LAN-Manager und NTLMv1 dürfen nicht erlaubt werden, um Sicherheitsrisiken zu vermeiden.', 147, 'SYS.2.2.3.A9', 'PREVENTIVE', 'SHOULD'),
    (210, 'Datei- und Freigabeberechtigungen', 'Datei- und Freigabeberechtigungen unter Windows', 'Zugriffsrechte auf Dateien und Ordner sollten gemäß eines Berechtigungskonzepts konfiguriert werden. Dies schützt sensible Daten vor unautorisiertem Zugriff.', 147, 'SYS.2.2.3.A12', 'PREVENTIVE', 'SHOULD'),
    (211, 'Verzicht auf SmartScreen-Funktion', 'Einsatz der SmartScreen-Funktion', 'Die SmartScreen-Funktion auf Windows sollte deaktiviert werden, um die Übertragung personenbezogener Daten an Microsoft zu verhindern.', 147, 'SYS.2.2.3.A13', 'PREVENTIVE', 'SHOULD'),
    (212, 'Verzicht auf Sprachassistenten', 'Einsatz des Sprachassistenten Cortana', 'Cortana sollte deaktiviert werden, um die Privatsphäre der Benutzenden zu wahren.', 147, 'SYS.2.2.3.A14', 'PREVENTIVE', 'SHOULD'),
    (213, 'Synchronisationsmechanismen', 'Einsatz der Synchronisationsmechanismen unter Windows', 'Die Synchronisierung von Daten mit Microsoft Cloud-Diensten und das Teilen von WLAN-Passwörtern sollten deaktiviert werden, um Sicherheits- und Datenschutzvorgaben einzuhalten.', 147, 'SYS.2.2.3.A15', 'PREVENTIVE', 'SHOULD'),
    (214, 'Anbindung an Microsoft-Store', 'Anbindung von Windows an den Microsoft-Store', 'Der Microsoft Store sollte auf Verträglichkeit mit Datenschutz- und Sicherheitsvorgaben überprüft werden. Unnötige Funktionen sollten deaktiviert werden.', 147, 'SYS.2.2.3.A16', 'PREVENTIVE', 'SHOULD'),
    (215, 'Automatischen Anmeldung', 'Keine Speicherung von Daten zur automatischen Anmeldung', 'Die Speicherung von Kennwörtern und anderen Anmeldeinformationen sollte nicht erlaubt werden, um Missbrauch zu verhindern.', 147, 'SYS.2.2.3.A17', 'PREVENTIVE', 'SHOULD'),
    (216, 'Windows-Remoteunterstützung', 'Einsatz der Windows-Remoteunterstützung', 'Die Windows-Remoteunterstützung sollte nur nach expliziter Einladung und Zustimmung genutzt werden. Unnötige Funktionen sollten deaktiviert werden.', 147, 'SYS.2.2.3.A18', 'PREVENTIVE', 'SHOULD'),
    (217, 'Sicherer Fernzugriff über RDP', 'Sicherheit beim Fernzugriff über RDP', 'Remote-Desktopzugriffe (RDP), sollten durch RDP-Gateways abgesichert werden. Komfortfunktionen sollten nur aktiviert werden, wenn sie mit dem Schutzbedarf vereinbar sind.', 147, 'SYS.2.2.3.A19', 'PREVENTIVE', 'SHOULD'),
    (218, 'Einsatz der Benutzerkontensteuerung', 'Einsatz der Benutzerkontensteuerung UAC für privilegierte Konten', 'Die Benutzerkontensteuerung (UAC), sollte so konfiguriert werden, dass ein Gleichgewicht zwischen Bedienbarkeit und Sicherheitsniveau gewährleistet ist.', 147, 'SYS.2.2.3.A20', 'PREVENTIVE', 'SHOULD'),
    (219, 'Einsatz des Encrypting File Systems', 'Einsatz des Encrypting File Systems', 'Das Encrypting File System (EFS), sollte mit sicheren Passwörtern und restriktiven Zugriffsrechten genutzt werden. Der Wiederherstellungsagent sollte ein dediziertes Konto sein. Diese Maßnahmen schützen die sensiblen Daten vor unbefugtem Zugriff und stellen sicher, dass im Falle eines Datenverlusts eine Wiederherstellung ohne Sicherheitskompromisse möglich ist.', 147, 'SYS.2.2.3.A21', 'PREVENTIVE', 'SHOULD'),
    (220, 'Deaktivierung der PowerShell', 'Verwendung der Windows PowerShell', 'Die PowerShell und die WPS-Dateien sollten nur von Administrierenden ausgeführt werden können. Die PowerShell-Ausführung sollte zentral protokolliert und überwacht werden. Dies schützt vor Missbrauch durch unbefugte Personen und ermöglicht eine zentrale Nachvollziehbarkeit. Zudem sollte die Ausführung von PowerShell-Skripten mit "Set-ExecutionPolicy AllSigned" eingeschränkt werden, um die Ausführung unsignierter Skripte zu verhindern, wodurch die Systemsicherheit erheblich gesteigert wird.', 147, 'SYS.2.2.3.A22', 'PREVENTIVE', 'SHOULD'),
    (221, 'Schutz der Anmeldeinformationen', 'Erweiterter Schutz der Anmeldeinformationen unter Windows', 'Auf UEFI-basierten Systemen sollte SecureBoot verwendet werden, und der Status des geschützten Modus für den Local Credential Store LSA sollte beim Systemstart überwacht werden. Diese Maßnahmen stellen sicher, dass das System vor nicht autorisierten Änderungen geschützt bleibt und die Integrität gewährleistet wird. Für die Fernwartung mittels RDP sollte die "restrictedAdmin"-Option verwendet werden, um Sicherheitsrisiken bei der Authentisierung zu minimieren.', 147, 'SYS.2.2.3.A23', 'PREVENTIVE', 'SHOULD'),
    (222, 'Last-Access-Zeitstempels', 'Aktivierung des Last-Access-Zeitstempels', 'Es sollte geprüft werden, ob der Last-Access-Zeitstempel im Dateisystem aktiviert werden kann. Dies erleichtert die Analyse von Systemmissbrauchsfällen. Dabei sollten mögliche Auswirkungen wie Performance-Verlust oder Einschränkungen bei Backups berücksichtigt werden, um einen optimalen Betrieb zu gewährleisten.', 147, 'SYS.2.2.3.A24', 'PREVENTIVE', 'SHOULD'),
    (223, 'Fernzugriffsfunktionen für Hersteller', 'Umgang mit Fernzugriffsfunktionen der"Connected User Experience and Telemetry"', 'Die Komponente "Connected User Experience and Telemetry" (CUET), sollte aufgrund ihrer Funktionen und der Fernzugriffsmöglichkeit für Microsoft sorgfältig überwacht werden. Netzseitiges Logging und gegebenenfalls das Blockieren von Fernzugriffen minimieren potenzielle Sicherheitsrisiken und schützen die Privatsphäre der Nutzenden.', 147, 'SYS.2.2.3.A25', 'PREVENTIVE', 'SHOULD'),
    (224, 'Nutzung des Virtual Secure Mode', 'Nutzung des Virtual Secure Mode', 'Beim Einsatz des Virtual Secure Mode (VSM), sollte berücksichtigt werden, dass forensische Untersuchungen eingeschränkt oder erschwert werden können. Dies ist wichtig, um bei Sicherheitsvorfällen die richtigen Maßnahmen planen und umsetzen zu können.', 147, 'SYS.2.2.3.A26', 'PREVENTIVE', 'SHOULD'),
    (225, 'Planung des sicheren Einsatzes', 'Planung des sicheren Einsatzes von macOS', 'Die Einführung von macOS muss sorgfältig geplant werden, einschließlich der Datenablage, Datensicherung und Aktualisierungsprozesse. Dies stellt sicher, dass macOS nahtlos in die institutionelle IT-Infrastruktur integriert wird und alle Sicherheits- und Betriebsanforderungen erfüllt werden.', 149, 'SYS.2.4.A1', 'PREVENTIVE', 'MUST'),
    (226, 'Integrierte Sicherheitsfunktionen', 'Nutzung der integrierten Sicherheitsfunktionen von macOS', 'Die integrierten Schutzmechanismen von macOS wie "System Integrity Protection", "Xprotect" und "Gatekeeper" müssen aktiviert sein. Dies verhindert die Ausführung unsignierter Programme und schützt das System vor potenziellen Angriffen.', 149, 'SYS.2.4.A2', 'PREVENTIVE', 'MUST'),
    (227, 'Verwendung geeigneter Konten', ' Verwendung geeigneter Konten', 'Das Administrationskonto, das bei der Erstkonfiguration von macOS erstellt wird, darf nur zu administrativen Zwecken genutzt werden. Standardkonten für den normalen Betrieb minimieren Sicherheitsrisiken durch versehentliche oder unbefugte Änderungen am System.', 149, 'SYS.2.4.A3', 'PREVENTIVE', 'MUST'),
    (228, 'Festplattenverschlüsselung', 'Verwendung einer Festplattenverschlüsselung', 'Festplatten, insbesondere bei mobilen Macs, sollten verschlüsselt werden. Wird FileVault verwendet, darf das Schlüsselmaterial nicht online bei Apple gespeichert werden. Dies schützt sensible Daten vor unberechtigtem Zugriff und Datenverlust.', 149, 'SYS.2.4.A4', 'PREVENTIVE', 'SHOULD'),
    (229, 'Sicherheitskritische Funktionen', 'Deaktivierung sicherheitskritischer Funktionen von macOS', 'Ortungsdienste und automatische Ausführungsfunktionen wie das Öffnen heruntergeladener Dateien sollten deaktiviert werden, um die Kontrolle über sensible Daten und Prozesse zu bewahren.', 149, 'SYS.2.4.A5', 'PREVENTIVE', 'SHOULD'),
    (230, 'Verwendung aktueller Mac-Hardware', 'Verwendung aktueller Mac-Hardware', 'Neue Macs sollten aktuelle Modelle sein. Für ältere Macs sollte regelmäßig überprüft werden, ob sie noch Sicherheits-Updates erhalten. Nicht unterstützte Geräte sollten aus dem Betrieb genommen werden, um Sicherheitslücken zu vermeiden.', 149, 'SYS.2.4.A6', 'PREVENTIVE', 'SHOULD'),
    (231, 'Zwei-Faktor-Authentisierung', 'Zwei-Faktor-Authentisierung für Apple-ID', 'Die Zwei-Faktor-Authentisierung für die Apple-ID sollte aktiviert werden, um unbefugten Zugriff auf Konten und Daten zu verhindern.', 149, 'SYS.2.4.A7', 'PREVENTIVE', 'SHOULD'),
    (232, 'Eingeschränkte iCloud-Nutzung', 'Keine Nutzung von iCloud für schützenswerte Daten', 'Schützenswerte Daten sollten nicht über iCloud synchronisiert, sondern über selbst betriebene Dienste verwaltet werden. Dies erhöht die Datensicherheit und schützt vor unbefugtem Zugriff durch Dritte.', 149, 'SYS.2.4.A8', 'PREVENTIVE', 'SHOULD'),
    (233, 'Zusätzliche Schutzprogramme', 'Verwendung von zusätzlichen Schutzprogrammen unter macOS', 'Bei Bedarf sollten zusätzlich zu den integrierten Schutzmechanismen von macOS Virenschutzlösungen von Drittanbietern eingesetzt werden, um die Sicherheit in heterogenen Netzwerken zu gewährleisten.', 149, 'SYS.2.4.A9', 'PREVENTIVE', 'SHOULD'),
    (234, 'Aktivierung der Personal Firewall', 'Aktivierung der Personal Firewall unter macOS', 'Die integrierte Personal Firewall von macOS sollte aktiviert und geeignet konfiguriert werden, um das System vor unbefugten Zugriffen zu schützen.', 149, 'SYS.2.4.A10', 'PREVENTIVE', 'SHOULD'),
    (235, 'Sichere Geräteaussonderung', 'Geräteaussonderung von Macs', 'Bei der Aussonderung eines Macs sollten der NVRAM und der SMC zurückgesetzt werden. Dies stellt sicher, dass keine sensiblen Daten oder Konfigurationsreste auf dem Gerät verbleiben.', 149, 'SYS.2.4.A11', 'PREVENTIVE', 'SHOULD'),
    (236, 'Firmware-Kennwort und Boot-Schutz', 'Firmware-Kennwort und Boot-Schutz auf Macs', 'Auf älteren Macs sollte ein sicheres Firmware-Kennwort aktiviert werden, um unbefugtes Booten zu verhindern. Bei neueren Macs mit T2-Chip sollte ein Firmware-Passwort sowie die Option „Sicheres Starten“ aktiviert werden.', 149, 'SYS.2.4.A12', 'PREVENTIVE', 'SHOULD'),
    (237, 'Authentisierung von Nutzern', 'Authentisierung von Administrierenden und Benutzenden', 'Administrierende dürfen sich nicht im Normalbetrieb als „root“ anmelden. Stattdessen sollte „sudo“ verwendet werden, um die Sicherheit zu erhöhen und alle administrativen Aktionen nachvollziehbar zu protokollieren.', 148, 'SYS.2.3.A1', 'PREVENTIVE', 'MUST'),
    (238, 'Geeignete Distribution', 'Auswahl einer geeigneten Distribution', 'Ein geeignetes Unix-Derivat oder eine Linux-Distribution muss basierend auf Sicherheitsanforderungen und Einsatzzweck ausgewählt werden. Die Auswahl stellt sicher, dass alle benötigten Funktionen und Sicherheitsfeatures verfügbar sind.', 148, 'SYS.2.3.A2', 'PREVENTIVE', 'MUST'),
    (239, 'Kernel-Aktualisierungen', 'Kernel-Aktualisierungen auf unixartigen Systemen', 'Nach einem Kernel-Update muss das System zeitnah neu gestartet werden. Alternativ kann Live-Patching aktiviert werden, um Sicherheitslücken sofort zu schließen, ohne den Betrieb zu unterbrechen.', 148, 'SYS.2.3.A4', 'PREVENTIVE', 'MUST'),
    (240, 'Sichere Anwendungsinstallation', 'Sichere Installation von Software-Paketen', 'Software, die aus Quellcode kompiliert wird, darf nur unter einem unprivilegierten Konto verarbeitet werden. Die Installationsschritte sollten dokumentiert werden, um eine reproduzierbare Installation zu gewährleisten und Risiken zu minimieren.', 148, 'SYS.2.3.A5', 'PREVENTIVE', 'MUST'),
    (241, 'Sicheres Einbinden von Laufwerken', 'Kein automatisches Einbinden von Wechsellaufwerken', 'Wechsellaufwerke sollten nicht automatisch eingebunden werden. Die Mount-Option „noexec“ sollte verwendet werden, um das Ausführen von Dateien zu verhindern und somit die Sicherheit zu erhöhen.', 148, 'SYS.2.3.A6', 'PREVENTIVE', 'SHOULD'),
    (242, 'Restriktive Rechtevergabe', 'Restriktive Rechtevergabe auf Dateien und Verzeichnisse', 'Dienste und Anwendungen sollten nur die ihnen zugeordneten Dateien bearbeiten dürfen. Das Sticky Bit sollte auf Verzeichnissen mit Schreibrechten gesetzt werden, um unerwünschte Änderungen durch andere Benutzende zu verhindern.', 148, 'SYS.2.3.A7', 'PREVENTIVE', 'SHOULD'),
    (243, 'Rechtebeschränkung von Anwendungen', 'Einsatz von Techniken zur Rechtebeschränkung von Anwendungen', 'Zur Rechtebeschränkung sollten Mechanismen wie AppArmor oder SELinux im Enforcing Mode verwendet werden. Diese Maßnahmen verhindern unberechtigte Zugriffe und minimieren Sicherheitsrisiken.', 148, 'SYS.2.3.A8', 'PREVENTIVE', 'SHOULD'),
    (244, 'Sicherer Passwortumgang', 'Sichere Verwendung von Passwörtern auf der Kommandozeile', 'Passwörter sollten nicht als Parameter an Programme übergeben werden. Dies schützt vor der unbeabsichtigten Offenlegung von sensiblen Informationen.', 148, 'SYS.2.3.A9', 'PREVENTIVE', 'SHOULD'),
    (245, 'Verhinderung der HDD-Überlastung', 'Verhinderung der Überlastung der lokalen Festplatte', 'Quotas für Konten und Dienste sollten eingerichtet werden, um eine ausgewogene Ressourcennutzung zu gewährleisten. Betriebssystem und Daten sollten auf separaten Partitionen gespeichert werden, um eine bessere Kontrolle und Sicherheit zu ermöglichen.', 148, 'SYS.2.3.A11', 'PREVENTIVE', 'SHOULD'),
    (246, 'Sicherer Einsatz von Appliances', 'Sicherer Einsatz von Appliances', 'Appliances sollten ein ähnliches Sicherheitsniveau wie Standard-Clients erfüllen. Wenn dies nicht nachweislich sichergestellt werden kann, sollte eine Konformitätserklärung des Herstellers angefordert werden.', 148, 'SYS.2.3.A12', 'PREVENTIVE', 'SHOULD'),
    (247, 'Nutzung unbefugter Peripheriegeräte', 'Absicherung gegen Nutzung unbefugter Peripheriegeräte', 'Peripheriegeräte sollten nur nutzbar sein, wenn sie explizit freigegeben sind. Dies verhindert unbefugte Zugriffe und die potenzielle Ausnutzung von Sicherheitslücken durch nicht autorisierte Hardware.', 148, 'SYS.2.3.A14', 'PREVENTIVE', 'SHOULD'),
    (248, 'Ausführung unerwünschter Dateien', 'Zusätzlicher Schutz vor der Ausführung unerwünschter Dateien', 'Partitionen mit Schreibrechten sollten so konfiguriert werden, dass keine Dateien ausführbar sind. Dies erhöht die Systemsicherheit und schützt vor Schadsoftware.', 148, 'SYS.2.3.A15', 'PREVENTIVE', 'SHOULD'),
    (249, 'Minimierte Schadensausbreitung', 'Zusätzliche Verhinderung der Ausbreitung bei der Ausnutzung  von Schwachstellen', 'Systemaufrufe sollten auf das notwendige Minimum beschränkt werden, insbesondere für exponierte Dienste und Anwendungen. Manuell überprüfte Profile und Regeln stellen sicher, dass nur notwendige Funktionen erlaubt sind.', 148, 'SYS.2.3.A17', 'PREVENTIVE', 'SHOULD'),
    (250, 'Zusätzlicher Schutz des Kernels', 'Zusätzlicher Schutz des Kernels', 'Speicherschutz, Dateisystemabsicherung und rollenbasierte Zugriffskontrolle sollten mit gehärteten Kernels wie grsecurity umgesetzt werden, um Schwachstellen auszuschließen und die Systemsicherheit zu erhöhen.', 148, 'SYS.2.3.A18', 'PREVENTIVE', 'SHOULD'),
    (251, 'Festplatten-/Dateiverschlüsselung', 'Festplatten- oder Dateiverschlüsselung', 'Festplatten sollten verschlüsselt werden. Die Schlüssel sollten nicht auf dem IT-System gespeichert werden, um die Sicherheit der Daten zu gewährleisten. Authenticated Encryption mit Associated Data (AEAD), oder „dm-crypt“ in Kombination mit „dm-verity“ bietet zusätzlichen Schutz.', 148, 'SYS.2.3.A19', 'PREVENTIVE', 'SHOULD'),
    (252, 'Kritische SysRq-Funktionen', 'Abschaltung kritischer SysRq-Funktionen', 'Es sollte festgelegt werden, welche SysRq-Funktionen von den Benutzenden ausgeführt werden dürfen. Generell sollten keine kritischen SysRq-Funktionen von den Benutzenden ausgelöst werden können. Dies dient dazu, potenziell schädliche Änderungen am System durch nicht autorisierte Personen zu verhindern und die Systemsicherheit zu erhöhen.', 148, 'SYS.2.3.A20', 'PREVENTIVE', 'SHOULD'),
    (253, 'Sichere E-Mail-Clients', 'Sichere Konfiguration der E-Mail-Clients', 'Die Institution muss eine sichere Konfiguration für die E-Mail-Clients vorgeben. Die E-Mail-Clients müssen den Benutzenden vorkonfiguriert zur Verfügung gestellt werden. Dies gewährleistet, dass sicherheitsrelevante Einstellungen standardisiert sind. Die Institution sollte sicherstellen, dass sicherheitsrelevante Teile der Konfiguration nicht von Benutzenden geändert werden können.', 154, 'APP.5.3.A1', 'PREVENTIVE', 'MUST'),
    (254, 'Sichere E-Mail-Servern', 'Sicherer Betrieb von E-Mail-Servern', 'Für den E-Mail-Empfang über nicht vertrauenswürdige Netze müssen E-Mail-Server eine sichere Transportverschlüsselung anbieten. Der Empfang von E-Mails über unverschlüsselte Verbindungen sollte deaktiviert werden. Dies verhindert das Abfangen sensibler Daten während der Übertragung. E-Mail-Server sollten sichere Transportverschlüsselungen auch beim Versenden von E-Mails nutzen, um den Datenschutz zu gewährleisten.', 154, 'APP.5.3.A2', 'PREVENTIVE', 'MUST'),
    (255, 'Datensicherung und Archivierung', 'Datensicherung und Archivierung von E-Mails', 'Der IT-Betrieb muss die Daten der E-Mail-Server und -Clients regelmäßig sichern. Die Institution muss regeln, wie gesendete und empfangene E-Mails gesichert werden, um Datenverluste zu vermeiden. Dies sorgt für die Verfügbarkeit und Nachvollziehbarkeit von E-Mails im Falle von Systemausfällen.', 154, 'APP.5.3.A3', 'PREVENTIVE', 'MUST'),
    (256, 'Spam- und Virenschutz', 'Spam- und Virenschutz auf dem E-Mail-Server', 'Der IT-Betrieb muss sicherstellen, dass E-Mails auf Schadsoftware und Spam-Merkmale überprüft werden. Die Einführung von E-Mail-Filterprogrammen muss mit Datenschutzbeauftragten und Personalvertretung abgestimmt werden. Dies schützt die Institution vor Schadsoftware und ungewollten E-Mails, während datenschutzrechtliche Vorgaben eingehalten werden.', 154, 'APP.5.3.A4', 'PREVENTIVE', 'MUST'),
    (257, 'Vertretungsregelungen für E-Mails', 'Festlegung von Vertretungsregelungen bei E-Mail-Nutzung', 'Die Institution sollte Vertretungsregelungen für die Bearbeitung von E-Mails festlegen. Bei Weiterleitungen müssen datenschutzrechtliche Aspekte berücksichtigt werden. Dies verhindert unautorisierte Weitergabe sensibler Informationen und stellt sicher, dass Vertretungen reibungslos erfolgen.', 154, 'APP.5.3.A5', 'PREVENTIVE', 'SHOULD'),
    (258, 'Festlegung einer Richtlinie', 'Festlegung einer Sicherheitsrichtlinie für E-Mail', 'Die Institution sollte eine Sicherheitsrichtlinie für die Nutzung von E-Mails erstellen und regelmäßig aktualisieren. Diese Richtlinie dient dazu, klare Vorgaben zur sicheren Nutzung von E-Mails zu schaffen und Benutzende sowie Administrierende über Sicherheitsvorgaben zu informieren.', 154, 'APP.5.3.A6', 'PREVENTIVE', 'SHOULD'),
    (259, 'Sicherheitsschulung', 'Schulung zu Sicherheitsmechanismen von E-Mail-Clients für Benutzende', 'Die Institution sollte das Personal über Risiken im Umgang mit E-Mail-Anwendungen aufklären. Schulungen und Sensibilisierung tragen dazu bei, dass Benutzende sich sicherheitsbewusst verhalten und potenzielle Gefahren durch E-Mails erkennen können.', 154, 'APP.5.3.A7', 'PREVENTIVE', 'SHOULD'),
    (260, 'Umgang mit Spam durch Benutzende', 'Umgang mit Spam durch Benutzende', 'Benutzende sollten Spam-E-Mails ignorieren und löschen, um die Gefahr von Phishing und Schadsoftware zu minimieren. Links in solchen E-Mails sollten nicht gefolgt werden, und unerwünschte E-Mails sollten nicht beantwortet werden, um den Absendern keine weiteren Informationen zu liefern.', 154, 'APP.5.3.A8', 'PREVENTIVE', 'SHOULD'),
    (261, 'Erweiterte Sicherheitsmaßnahmen', 'Erweiterte Sicherheitsmaßnahmen auf dem E-Mail-Server', 'E-Mail-Server sollten eingehende E-Mails mittels SPF und DKIM überprüfen. Die Institution sollte selbst DKIM und SPF einsetzen, um die Authentizität versendeter E-Mails sicherzustellen und den Schutz vor Phishing zu erhöhen.', 154, 'APP.5.3.A9', 'PREVENTIVE', 'SHOULD'),
    (262, 'Verschlüsselung und Signatur', 'Ende-zu-Ende-Verschlüsselung und Signatur', 'Die Institution sollte Ende-zu-Ende-Verschlüsselung und digitale Signaturen für E-Mails einsetzen. Dies schützt die Vertraulichkeit und Integrität der E-Mails und stellt sicher, dass nur die vorgesehenen Empfänger die Inhalte lesen können.', 154, 'APP.5.3.A10', 'PREVENTIVE', 'SHOULD'),
    (263, 'Einsatz redundanter E-Mail-Server', 'Einsatz redundanter E-Mail-Server', 'Die Institution sollte redundante E-Mail-Server betreiben, um die Verfügbarkeit der E-Mail-Dienste sicherzustellen. Redundanz und geeignete Priorisierung in den MX-Records verhindern Unterbrechungen bei Ausfällen.', 154, 'APP.5.3.A11', 'PREVENTIVE', 'SHOULD'),
    (264, 'Überwachung von Block-Listen', 'Überwachung öffentlicher Block-Listen', 'Der IT-Betrieb sollte regelmäßig prüfen, ob die E-Mail-Server der Institution auf öffentlichen Spam- oder Blocklisten aufgeführt sind. Dies schützt die Reputation der Institution und verhindert, dass legitime E-Mails als Spam eingestuft werden.', 154, 'APP.5.3.A12', 'PREVENTIVE', 'SHOULD'),
    (265, 'TLS-Reporting', 'TLS-Reporting', 'Die Institution sollte TLS-Reporting einsetzen, um die Sicherheit von E-Mail-Verbindungen zu überwachen. Diese Berichte bieten Einblicke in potenzielle Sicherheitsprobleme bei der E-Mail-Kommunikation.', 154, 'APP.5.3.A13', 'PREVENTIVE', 'SHOULD'),
    (266, 'Einschränken von Aktiven Inhalten', 'Einschränken von Aktiven Inhalten', 'Eingebettete aktive Inhalte in E-Mails müssen deaktiviert werden, um Sicherheitsrisiken durch potenziell schädliche Skripte oder Inhalte zu minimieren. Falls aktive Inhalte erforderlich sind, dürfen sie nur aus vertrauenswürdigen Quellen ausgeführt werden.', 152, 'APP.1.1.A2', 'PREVENTIVE', 'MUST'),
    (267, 'Zugriff auf externe Quellen', 'Sicheres Öffnen von Dokumenten aus externen Quellen', 'Alle Dokumente aus externen Quellen müssen vor dem Öffnen auf Schadsoftware überprüft werden. Die Verwendung problematischer Dateiformate sollte verboten und blockiert werden, um Sicherheitsrisiken zu minimieren.', 152, 'APP.1.1.A3', 'PREVENTIVE', 'MUST'),
    (268, 'Spezifische Office-Eigenschaften', 'Sensibilisierung zu spezifischen Office-Eigenschaften', 'Benutzende müssen über die Gefahren durch aktive Inhalte in Office-Dateien und den sicheren Umgang mit externen Dokumenten sensibilisiert werden. Dies stellt sicher, dass sie Sicherheitsrisiken erkennen und vermeiden können.', 152, 'APP.1.1.A17', 'PREVENTIVE', 'MUST'),
    (269, 'Testen neuer Versionen', 'Testen neuer Versionen von Office-Produkten', 'Neue Versionen von Office-Produkten sollten auf Kompatibilität mit etablierten Arbeitsmitteln geprüft werden. Dies gewährleistet, dass wichtige Arbeitsprozesse reibungslos fortgeführt werden können.', 152, 'APP.1.1.A6', 'PREVENTIVE', 'SHOULD'),
    (270, 'Regelung für Software-Entwicklung', 'Regelung der Software-Entwicklung durch Endbenutzende', 'Für die Software-Entwicklung auf Basis von Office-Anwendungen sollten verbindliche Regelungen getroffen werden. Dies stellt sicher, dass Eigenentwicklungen sicher und kontrolliert erfolgen und ihre Nutzung dokumentiert wird.', 152, 'APP.1.1.A10', 'PREVENTIVE', 'SHOULD'),
    (271, 'Sicherer Einsatz von Erweiterungen', 'Geregelter Einsatz von Erweiterungen für Office-Produkte', 'Erweiterungen von Office-Produkten sollten vor dem Einsatz getestet werden, um potenzielle negative Auswirkungen auf die IT-Systeme zu vermeiden. Testpläne sollten eine nachvollziehbare Dokumentation des Vorgehens sicherstellen.', 152, 'APP.1.1.A11', 'PREVENTIVE', 'SHOULD'),
    (272, 'Verzicht auf Cloud-Speicherung', 'Verzicht auf Cloud-Speicherung', 'Die Funktionen für Cloud-Speicher in Office-Produkten sollten deaktiviert werden. Stattdessen sollten Dokumente auf zentral verwalteten Fileservern gespeichert werden, um die Sicherheit und Kontrolle zu erhöhen.', 152, 'APP.1.1.A12', 'PREVENTIVE', 'SHOULD'),
    (273, 'Verwendung von Viewer-Funktionen', 'Verwendung von Viewer-Funktionen', 'Daten aus potenziell unsicheren Quellen sollten automatisch in einem geschützten Modus geöffnet werden. Dieser Modus minimiert die Ausführung potenziell schädlicher Inhalte und schützt die Integrität des Systems.', 152, 'APP.1.1.A13', 'PREVENTIVE', 'SHOULD'),
    (274, 'Nachträgliche Veränderungen', 'Schutz gegen nachträgliche Veränderungen von Dokumenten', 'Dokumente sollten gegen nachträgliche Veränderungen geschützt werden. Dies gewährleistet die Integrität der Inhalte und verhindert Manipulationen.', 152, 'APP.1.1.A14', 'PREVENTIVE', 'SHOULD'),
    (275, 'Verschlüsselung und Signaturen', 'Einsatz von Verschlüsselung und Digitalen Signaturen', 'Daten mit erhöhtem Schutzbedarf sollten verschlüsselt gespeichert oder übertragen werden. Dies stellt sicher, dass sensible Informationen auch bei einem möglichen Abfangen der Übertragung geschützt bleiben.', 152, 'APP.1.1.A15', 'PREVENTIVE', 'SHOULD'),
    (276, 'Integritätsprüfung von Dokumenten', 'Integritätsprüfung von Dokumenten', 'Für den Schutz von Daten vor Manipulation sollten geeignete kryptografische Verfahren zur Integritätsprüfung eingesetzt werden. Dies gewährleistet die Unverfälschtheit der Daten.', 152, 'APP.1.1.A16', 'PREVENTIVE', 'SHOULD'),
    (277, 'Anforderungsanalyse', 'Anforderungsanalyse für die Nutzung von Apps', 'Die Anforderungsanalyse muss Risiken der mobilen Nutzung berücksichtigen. Dies stellt sicher, dass potenzielle Sicherheitsprobleme frühzeitig identifiziert und adressiert werden.', 153, 'APP.1.4.A1', 'PREVENTIVE', 'MUST'),
    (278, 'Kontrolle von App-Berechtigungen', 'Minimierung und Kontrolle von App-Berechtigungen', 'Sicherheitsrelevante Berechtigungseinstellungen müssen fixiert werden, um unautorisierte Änderungen durch Benutzende oder Apps zu verhindern. Regelmäßige Prüfungen gewährleisten die Einhaltung der Sicherheitsvorgaben.', 153, 'APP.1.4.A5', 'PREVENTIVE', 'MUST'),
    (279, 'Speicherung lokaler App-Daten', 'Sichere Speicherung lokaler App-Daten', 'Wenn Apps auf interne Dokumente der Institution zugreifen können, muss die lokale Datenhaltung angemessen abgesichert sein. Verschlüsselte Speicherung von Zugriffsschlüsseln verhindert unberechtigten Zugriff auf sensible Daten.', 153, 'APP.1.4.A7', 'PREVENTIVE', 'MUST'),
    (280, 'Verhinderung von Datenabfluss', 'Verhinderung von Datenabfluss', 'Um zu verhindern, dass Apps ungewollt vertrauliche Daten versenden oder aus den gesendeten Daten Profile über die Benutzenden erstellt werden, muss die App-Kommunikation geeignet eingeschränkt werden. Dazu sollte die Kommunikation im Rahmen des Test- und Freigabeverfahrens analysiert werden. Weiterhin sollte überprüft werden, ob eine App ungewollte Protokollierungs- oder Hilfsdateien schreibt, die möglicherweise vertrauliche Informationen enthalten. Dies schützt sensible Daten der Institution und verhindert ungewollte Informationsweitergaben.', 153, 'APP.1.4.A8', 'PREVENTIVE', 'MUST'),
    (281, 'Verteilung schutzbedürftiger Apps', 'Verteilung schutzbedürftiger Apps', 'Interne Apps der Institution und Apps, die schutzbedürftige Informationen verarbeiten, sollten über einen institutionseigenen App Store oder via MDM verteilt werden. Dies stellt sicher, dass nur geprüfte und autorisierte Apps verwendet werden und erhöht die Sicherheitsstandards.', 153, 'APP.1.4.A3', 'PREVENTIVE', 'SHOULD'),
    (282, 'Sichere Deinstallation von Apps', 'Sichere Deinstallation von Apps', 'Werden Apps deinstalliert, sollten auch Daten gelöscht werden, die auf externen Systemen, beispielsweise bei den App-Anbietenden, gespeichert wurden. Dies verhindert, dass sensible Informationen nach der Deinstallation einer App weiterhin zugänglich bleiben.', 153, 'APP.1.4.A12', 'PREVENTIVE', 'SHOULD'),
    (283, 'Authentisierungsmerkmale bei Apps', 'Unterstützung zusätzlicher Authentisierungsmerkmale bei Apps', 'Falls möglich, sollte für die Authentisierung in Apps ein zweiter Faktor benutzt werden. Hierbei sollte darauf geachtet werden, dass eventuell benötigte Sensoren oder Schnittstellen in allen verwendeten Geräten vorhanden sind. Zusätzlich sollte bei biometrischen Verfahren berücksichtigt werden, wie resistent die Authentisierung gegen mögliche Fälschungsversuche ist. Dies erhöht die Sicherheit und verhindert unbefugten Zugriff.', 153, 'APP.1.4.A14', 'PREVENTIVE', 'SHOULD'),
    (284, 'Penetrationstests für Apps', 'Durchführung von Penetrationstests für Apps', 'Bevor eine App für den Einsatz freigegeben wird, sollte ein Penetrationstest durchgeführt werden. Dabei sollten alle Kommunikationsschnittstellen zu Backend-Systemen sowie die lokale Speicherung von Daten auf mögliche Sicherheitslücken untersucht werden. Die Penetrationstests sollten regelmäßig und zusätzlich bei größeren Änderungen an der App wiederholt werden. Dies dient dazu, Sicherheitslücken frühzeitig zu erkennen und zu beheben.', 153, 'APP.1.4.A15', 'PREVENTIVE', 'SHOULD'),
    (285, 'Mobile Application Management', 'Mobile Application Management', 'Falls möglich, sollte für das zentrale Konfigurieren von dienstlichen Apps ein Mobile Application Management verwendet werden. Dies ermöglicht eine einheitliche und sichere Verwaltung der Apps.', 153, 'APP.1.4.A16', 'PREVENTIVE', 'SHOULD'),
    (286, 'Sicherheitsmechanismen', 'Verwendung von grundlegenden Sicherheitsmechanismen', 'Der eingesetzte Webbrowser muss sicherstellen, dass jede Instanz und jeder Verarbeitungsprozess nur auf die eigenen Ressourcen zugreifen kann (Sandboxing),. Webseiten müssen als eigenständige Prozesse oder mindestens als eigene Threads voneinander isoliert werden. Plug-ins und Erweiterungen müssen ebenfalls in isolierten Bereichen ausgeführt werden. Der verwendete Webbrowser muss die Content Security Policy (CSP), umsetzen. Der aktuell höchste Level der CSP sollte erfüllt werden. Der Browser muss Maßnahmen zur Same-Origin-Policy und Subresource Integrity unterstützen. Dies schützt vor Sicherheitsrisiken durch Webseiten und Erweiterungen.', 155, 'APP.1.2.A1', 'PREVENTIVE', 'MUST'),
    (287, 'Sichere Verschlüsselung', 'Unterstützung sicherer Verschlüsselung der Kommunikation', 'Der Webbrowser muss Transport Layer Security (TLS), in einer sicheren Version unterstützen. Verbindungen zu Webservern müssen mit TLS verschlüsselt werden, falls dies vom Webserver unterstützt wird. Unsichere Versionen von TLS sollten deaktiviert werden. Der Webbrowser muss den Sicherheitsmechanismus HTTP Strict Transport Security (HSTS), gemäß RFC 6797 unterstützen und einsetzen. Dies sichert die Kommunikation zwischen Browser und Server.', 155, 'APP.1.2.A2', 'PREVENTIVE', 'MUST'),
    (288, 'Vertrauenswürdige Zertifikate', 'Verwendung von vertrauenswürdigen Zertifikaten', 'Falls der Webbrowser eine eigene Liste von vertrauenswürdigen Wurzelzertifikaten bereitstellt, muss sichergestellt werden, dass nur der IT-Betrieb diese ändern kann. Falls dies nicht durch technische Maßnahmen möglich ist, muss den Benutzenden verboten werden, diese Liste zu ändern. Außerdem muss sichergestellt werden, dass der Webbrowser Zertifikate lokal widerrufen kann. Der Webbrowser muss die Gültigkeit der Server-Zertifikate mithilfe des öffentlichen Schlüssels und unter Berücksichtigung des Gültigkeitszeitraums vollständig prüfen. Auch der Sperrstatus der Server-Zertifikate muss vom Webbrowser geprüft werden. Die Zertifikatskette einschließlich des Wurzelzertifikats muss verifiziert werden. Der Webbrowser muss den Benutzenden eindeutig und gut sichtbar darstellen, ob die Kommunikation im Klartext oder verschlüsselt erfolgt. Der Webbrowser sollte den Benutzenden auf Anforderung das verwendete Serverzertifikat anzeigen können. Der Webbrowser muss den Benutzenden signalisieren, wenn Zertifikate fehlen, ungültig sind oder widerrufen wurden. Der Webbrowser muss in diesem Fall die Verbindung abbrechen, bis die Benutzenden diese ausdrücklich bestätigt haben. Dies schützt die Integrität und Sicherheit von Verbindungen.', 155, 'APP.1.2.A3', 'PREVENTIVE', 'MUST'),
    (289, 'Kennwortmanagement im Webbrowser', 'Kennwortmanagement im Webbrowser', 'Wird ein Kennwortmanager im Webbrowser verwendet, muss er eine direkte und eindeutige Beziehung zwischen Webseite und hierfür gespeichertem Kennwort herstellen. Der Kennwortspeicher muss die Passwörter verschlüsselt speichern. Es muss sichergestellt werden, dass auf die im Kennwortmanager gespeicherten Passwörter nur nach Eingabe eines Master-Kennworts zugegriffen werden kann. Außerdem muss sichergestellt sein, dass die Authentisierung für den kennwortgeschützten Zugriff nur für die aktuelle Sitzung gültig ist. Der IT-Betrieb muss sicherstellen, dass der verwendete Browser den Benutzenden die Möglichkeit bietet, gespeicherte Passwörter zu löschen. Dies schützt die gespeicherten Anmeldeinformationen und erhöht die Sicherheit.', 155, 'APP.1.2.A6', 'PREVENTIVE', 'MUST'),
    (290, 'Nutzung von DNS-over-HTTPS', 'Nutzung von DNS-over-HTTPS', 'Die Institution muss entscheiden, ob die verwendeten Browser DNS-over-HTTPS (DoH), verwenden sollen. Die Browser müssen entsprechend dieser Entscheidung konfiguriert werden. Falls ein interner DNS-Resolver verwendet wird, muss dieser auch vom Browser verwendet werden. Dies stellt sicher, dass DNS-Anfragen sicher und kontrolliert verarbeitet werden.', 155, 'APP.1.2.A13', 'PREVENTIVE', 'MUST'),
    (291, 'Datensparsamkeit in Webbrowsern', 'Datensparsamkeit in Webbrowsern', 'Cookies von Drittanbietern sollten abgelehnt werden, und Benutzende sollten gespeicherte Cookies, Autovervollständigungsdaten sowie Historien löschen können. Funktionen wie Telemetrie und Standortfreigaben sollten deaktiviert werden. Dies verhindert das Tracking und schützt sensible Daten vor unbefugtem Zugriff.', 155, 'APP.1.2.A7', 'PREVENTIVE', 'SHOULD'),
    (292, 'Isolierte Webbrowser-Umgebung', 'Einsatz einer isolierten Webbrowser-Umgebung', 'Die Institution sollte isolierte und abgesicherte Browserumgebungen nutzen, wie virtualisierte Instanzen. Erhöht die Sicherheit und schützt vor Bedrohungen aus dem Internet.', 155, 'APP.1.2.A9', 'PREVENTIVE', 'SHOULD'),
    (293, 'Verwendung des privaten Modus', 'Verwendung des privaten Modus', 'Browser sollten im privaten Modus betrieben werden, und lokale Inhalte sollten beim Beenden gelöscht werden. Schützt die Vertraulichkeit der Benutzenden und minimiert gespeicherte Daten.', 155, 'APP.1.2.A10', 'PREVENTIVE', 'SHOULD'),
    (294, 'Überprüfung auf schädliche Inhalte', 'Überprüfung auf schädliche Inhalte', 'Webbrowser sollten potenziell schädliche Inhalte prüfen, warnen und den Zugriff bei als schädlich eingestuften Verbindungen blockieren. Dies reduziert das Risiko von Malware-Infektionen und anderen Angriffen.', 155, 'APP.1.2.A11', 'PREVENTIVE', 'SHOULD'),
    (295, 'Zwei-Browser-Strategie', 'Zwei-Browser-Strategie', 'Ein alternativer Browser sollte bereitgestellt werden, falls ungelöste Sicherheitsprobleme mit dem Hauptbrowser auftreten. Sichert die Kontinuität der Arbeit und bietet Ausweichmöglichkeiten bei Sicherheitsproblemen.', 155, 'APP.1.2.A12', 'PREVENTIVE', 'SHOULD'),
    (296, 'Authentisierung', 'Authentisierung', 'Webanwendungen und Webservices müssen eine Client-Authentisierung erfordern, mit dokumentierter Auswahl einer Authentisierungsmethode und festgelegten Grenzwerten für Anmeldeversuche. Garantiert den Schutz geschützter Ressourcen vor unberechtigtem Zugriff.', 156, 'APP.3.1.A1', 'PREVENTIVE', 'MUST'),
    (297, 'Kontrollierte Einbindung', 'Kontrolliertes Einbinden von Dateien und Inhalten', 'Upload-Funktionen müssen auf Dateigröße, Typ und Speicherort eingeschränkt werden, mit restriktiven Zugriffsrechten. Minimiert Sicherheitsrisiken durch unkontrollierte Datei-Uploads.', 156, 'APP.3.1.A4', 'PREVENTIVE', 'MUST'),
    (298, 'Unerlaubte Automatisierung', 'Schutz vor unerlaubter automatisierter Nutzung', 'Webanwendungen und Webservices müssen vor unberechtigter automatisierter Nutzung geschützt werden, ohne die Nutzungsmöglichkeiten berechtigter Clients zu beeinträchtigen. Verhindert Missbrauch und unautorisierte Zugriffe.', 156, 'APP.3.1.A7', 'PREVENTIVE', 'MUST'),
    (299, 'Schutz vertraulicher Daten', 'Schutz vertraulicher Daten', 'Zugangsdaten für Webanwendungen und Webservices müssen serverseitig geschützt werden, etwa durch Salted Hash-Verfahren, und Quelltexte vor Abrufen gesichert werden. Garantiert den Schutz sensibler Daten und des Quellcodes.', 156, 'APP.3.1.A14', 'PREVENTIVE', 'MUST'),
    (300, 'Systemarchitektur', 'Systemarchitektur', 'Sicherheitsaspekte sollten bereits bei der Planung von Webanwendungen und Webservices berücksichtigt werden, um die Geschäftslogik korrekt umzusetzen. Sichert eine robuste und sichere Architektur.', 156, 'APP.3.1.A8', 'PREVENTIVE', 'SHOULD'),
    (301, 'Sichere Beschaffung', 'Beschaffung von Webanwendungen und Webservices', 'Bei der Beschaffung von Webanwendungen und Webservices sollten Aspekte wie sichere Eingabevalidierung, Berechtigungsmanagement und regelmäßige Sicherheitsupdates beachtet werden. Garantiert die Sicherheit und Effizienz der eingesetzten Lösungen.', 156, 'APP.3.1.A9', 'PREVENTIVE', 'SHOULD'),
    (302, 'Anbindung von Hintergrundsystemen', 'Sichere Anbindung von Hintergrundsystemen', 'Der Zugriff auf Hintergrundsysteme sollte nur über definierte Schnittstellen und verschlüsselte Kommunikation möglich sein. Schützt ausgelagerte Daten und Funktionen vor unautorisiertem Zugriff.', 156, 'APP.3.1.A11', 'PREVENTIVE', 'SHOULD'),
    (303, 'Sichere Konfiguration', 'Sichere Konfiguration', 'Webanwendungen und Webservices sollten nur über abgesicherte Kommunikationspfade zugänglich sein, mit deaktivierten oder eingeschränkten unnötigen Ressourcen. Verhindert Sicherheitslücken und reduziert die Angriffsfläche.', 156, 'APP.3.1.A12', 'PREVENTIVE', 'SHOULD'),
    (304, 'Sichere HTTP-Konfiguration', 'Sichere HTTP-Konfiguration bei Webanwendungen', 'HTTP-Response-Header wie CSP und HSTS sollten gesetzt werden, Cookies sollten sicher konfiguriert sein. Schützt vor Angriffen wie Clickjacking und Cross-Site-Scripting.', 156, 'APP.3.1.A21', 'PREVENTIVE', 'SHOULD'),
    (305, 'Penetrationstest und Revision', 'Penetrationstest und Revision', 'Webanwendungen und Webservices sollten regelmäßig überprüft und dokumentiert werden, um Abweichungen zu identifizieren und anzugehen. Garantiert die kontinuierliche Sicherheit und Konformität.', 156, 'APP.3.1.A22', 'PREVENTIVE', 'SHOULD'),
    (306, 'Web Application Firewalls', 'Einsatz von Web Application Firewalls', 'Web Application Firewalls (WAF), sollten eingesetzt und auf die geschützten Anwendungen angepasst werden, mit regelmäßigen Überprüfungen nach Updates. Bietet einen zusätzlichen Schutzschild vor Web-basierten Angriffen.', 156, 'APP.3.1.A20', 'PREVENTIVE', 'SHOULD');
INSERT INTO it_control_guidelines (position, control_id, description)
VALUES
    (1, 14, 'Die Institutionsleitung MUSS ausreichend für Sicherheitsfragen sensibilisiert werden.'),
    (2, 14, 'Die Sicherheitskampagnen und Schulungsmaßnahmen MÜSSEN von der Institutionsleitung unterstützt werden.'),
    (3, 14, 'Vor dem Beginn eines Sensibilisierungs- und Schulungsprogramms zur Informationssicherheit MUSS die Unterstützung der Institutionsleitung eingeholt werden.'),
    (4, 14, 'Alle Vorgesetzten MÜSSEN die Informationssicherheit unterstützen, indem sie mit gutem Beispiel vorangehen.'),
    (5, 14, 'Führungskräfte MÜSSEN die Sicherheitsvorgaben umsetzen.'),
    (6, 14, 'Hierüber hinaus MÜSSEN sie ihre Mitarbeitenden auf deren Einhaltung hinweisen.'),
    (1, 15, 'Alle Mitarbeitenden und externen Benutzenden MÜSSEN in den sicheren Umgang mit IT-, ICS- und IoT-Komponenten eingewiesen und sensibilisiert werden, soweit dies für ihre Arbeitszusammenhängerelevant ist.'),
    (2, 15, 'Dafür MÜSSEN verbindliche, verständliche und aktuelle Richtlinien zur Nutzung derjeweiligen Komponenten zur Verfügung stehen.'),
    (3, 15, 'Werden IT-, ICS- oder IoT-Systeme oder -Dienste in einer Weise benutzt, die den Interessen der Institution widersprechen, MUSS dies kommuniziert werden.'),
    (1, 16, 'Sensibilisierungs- und Schulungsprogramme zur Informationssicherheit SOLLTEN sich an denjeweiligen Zielgruppen orientieren. Dazu SOLLTE eine Zielgruppenanalyse durchgeführt werden. Hierbei SOLLTEN Schulungsmaßnahmen auf die speziellen Anforderungen und unterschiedlichenHintergründe fokussiert werden können.'),
    (2, 16, 'Es SOLLTE ein zielgruppenorientiertes Sensibilisierungs- und Schulungsprogramm zur Informationssicherheit erstellt werden.'),
    (3, 16, 'Dieses Schulungsprogramm SOLLTE den Mitarbeitenden alle Informationen und Fähigkeiten vermitteln, die erforderlich sind, um in der Institution geltendeSicherheitsregelungen und -maßnahmen umsetzen zu können.'),
    (4, 16, 'Es SOLLTE regelmäßig überprüft undaktualisiert werden.'),
    (1, 17, 'Alle Mitarbeitenden SOLLTEN entsprechend ihren Aufgaben und Verantwortlichkeiten zuInformationssicherheitsthemen geschult werden.'),
    (1, 18, 'Informationssicherheitsbeauftragte SOLLTEN mit dem IT-Grundschutz vertraut sein.'),
    (2, 18, 'Wurde einSchulungsbedarf identifiziert, SOLLTE eine geeignete IT-Grundschutz-Schulung geplant werden.'),
    (3, 18, 'Für die Planung einer Schulung SOLLTE der Online-Kurs des BSI zum IT-Grundschutz berücksichtigt werden.'),
    (4, 18, 'Innerhalb der Schulung SOLLTE die Vorgehensweise anhand praxisnaher Beispiele geübt werden.'),
    (5, 18, 'Es SOLLTE geprüft werden, ob der oder die Informationssicherheitsbeauftragte sich zu einemBSI IT-Grundschutz-Praktiker qualifizieren lassen sollten.'),
    (1, 19, 'Die Lernerfolge im Bereich Informationssicherheit SOLLTEN zielgruppenbezogen gemessen undausgewertet werden, um festzustellen, inwieweit die in den Sensibilisierungs- und Schulungsprogrammen zur Informationssicherheit beschriebenen Ziele erreicht sind.'),
    (2, 19, 'Die MessungenSOLLTEN sowohl quantitative als auch qualitative Aspekte der Sensibilisierungs- undSchulungsprogramme zur Informationssicherheit berücksichtigen.'),
    (3, 19, 'Die Ergebnisse SOLLTEN bei derVerbesserung des Sensibilisierungs- und Schulungsangebots zur Informationssicherheit in geeigneterWeise einfließen.'),
    (4, 19, 'Der oder die Informationssicherheitsbeauftragte SOLLTE sich regelmäßig mit der Personalabteilungund den anderen für die Sicherheit relevanten Ansprechpartnern (Datenschutz, Gesundheits- undArbeitsschutz, Brandschutz etc.) über die Effizienz der Aus- und Weiterbildung austauschen.'),
    (1, 20, 'Besonders exponierte Personen SOLLTEN vertiefende Schulungen in Hinblick auf möglicheGefährdungen sowie geeignete Verhaltensweisen und Vorsichtsmaßnahmen erhalten.'),
    (1, 21, 'Die Personalabteilung sowie die Vorgesetzten MÜSSEN dafür sorgen, dass Mitarbeitende zu Beginnihrer Beschäftigung in ihre neuen Aufgaben eingearbeitet werden.'),
    (2, 21, 'Die Mitarbeitenden MÜSSEN überbestehende Regelungen, Handlungsanweisungen und Verfahrensweisen informiert werden.'),
    (3, 21, 'EineCheckliste und ein direkter Ansprechpartner oder Ansprechpartnerin („Pate oder Patin“) kann hierbeihilfreich sein und SOLLTE etabliert werden.'),
    (1, 22, 'Verlassen Mitarbeitende die Institution, MUSS der oder die Nachfolgende rechtzeitig eingewiesen werden.'),
    (2, 22, 'Dies SOLLTE idealerweise durch den oder die ausscheidenden Mitarbeitenden erfolgen.'),
    (3, 22, 'Isteine direkte Übergabe nicht möglich, MUSS von den ausscheidenden Mitarbeitenden eine ausführliche Dokumentation angefertigt werden.'),
    (4, 22, 'Außerdem MÜSSEN von ausscheidenden Mitarbeitenden alle im Rahmen ihrer Tätigkeit erhaltenen Unterlagen, Schlüssel und Geräte sowie Ausweise und Zutrittsberechtigungen eingezogen werden.'),
    (5, 22, 'Vor der Verabschiedung MUSS noch einmal auf Verschwiegenheitsverpflichtungen hingewiesen werden.'),
    (6, 22, 'Es SOLLTE besonders darauf geachtet werden, dass keine Interessenkonflikte auftreten.'),
    (7, 22, 'Um nach einem Stellenwechsel Interessenkonflikte zu vermeiden, SOLLTEN Konkurrenzverbote und Karenzzeiten vereinbart werden.'),
    (8, 22, 'Weiterhin MÜSSEN Notfall- und andere Ablaufpläne aktualisiert werden.'),
    (9, 22, 'Alle betroffenen Stelleninnerhalb der Institution, wie z. B. das Sicherheitspersonal oder die IT-Abteilung, MÜSSEN über dasAusscheiden des oder der Mitarbeitenden informiert werden.'),
    (10, 22, 'Damit alle verbundenen Aufgaben, diebeim Ausscheiden des oder der Mitarbeitenden anfallen, erledigt werden, SOLLTE hier ebenfalls eineCheckliste angelegt werden.'),
    (11, 22, 'Zudem SOLLTE es einen festen Ansprechpartner oder Ansprechpartnerinder Personalabteilung geben, der den Weggang von Mitarbeitenden begleitet.'),
    (1, 23, 'Die Vorgesetzten MÜSSEN dafür sorgen, dass im laufenden Betrieb Vertretungsregelungen umgesetzt werden.'),
    (2, 23, 'Dafür MUSS sichergestellt werden, dass es für alle wesentlichen Geschäftsprozesse undAufgaben praktikable Vertretungsregelungen gibt.'),
    (3, 23, 'Bei diesen Regelungen MUSS der Aufgabenumfangder Vertretung im Vorfeld klar definiert werden.'),
    (4, 23, 'Es MUSS sichergestellt werden, dass die Vertretungüber das dafür nötige Wissen verfügt.'),
    (5, 23, 'Ist dies nicht der Fall, MUSS überprüft werden, wie derVertretenden zu schulen ist oder ob es ausreicht, den aktuellen Verfahrens- oder Projektstandausreichend zu dokumentieren.'),
    (6, 23, 'Ist es im Ausnahmefall nicht möglich, für einzelne Mitarbeitendeeinen kompetenten Vertretenden zu benennen oder zu schulen, MUSS frühzeitig entschieden werden,ob externes Personal dafür hinzugezogen werden kann.'),
    (1, 24, 'Wird externes Personal beschäftigt, MUSS dieses wie alle eigenen Mitarbeitenden dazu verpflichtet werden, geltende Gesetze, Vorschriften und interne Regelungen einzuhalten.'),
    (2, 24, 'Fremdpersonal, daskurzfristig oder einmalig eingesetzt wird, MUSS in sicherheitsrelevanten Bereichen beaufsichtigt werden.'),
    (3, 24, 'Bei längerfristig beschäftigtem Fremdpersonal MUSS dieses wie die eigenen Mitarbeitenden inseine Aufgaben eingewiesen werden.'),
    (4, 24, 'Auch für diese Mitarbeitende MUSS eine Vertretungsregelungeingeführt werden.'),
    (5, 24, 'Verlässt das Fremdpersonal die Institution, MÜSSEN Arbeitsergebnisse wie beieigenem Personal geregelt übergeben und eventuell ausgehändigte Zugangsberechtigungenzurückgegeben werden.'),
    (1, 25, 'Bevor externe Personen Zugang und Zugriff zu vertraulichen Informationen erhalten, MÜSSEN mitihnen Vertraulichkeitsvereinbarungen in schriftlicher Form geschlossen werden.'),
    (2, 25, 'In diesenVertraulichkeitsvereinbarungen MÜSSEN alle wichtigen Aspekte zum Schutz von institutionsinternenInformationen berücksichtigt werden.'),
    (1, 26, 'Alle Mitarbeitenden MÜSSEN dazu verpflichtet werden, geltende Gesetze, Vorschriften und interneRegelungen einzuhalten.'),
    (2, 26, 'Den Mitarbeitenden MUSS der rechtliche Rahmen ihre Tätigkeit bekanntsein.'),
    (3, 26, 'Die Aufgaben und Zuständigkeiten von Mitarbeitenden MÜSSEN in geeigneter Weisedokumentiert sein.'),
    (4, 26, 'Außerdem MÜSSEN alle Mitarbeitenden darauf hingewiesen werden, dass allewährend der Arbeit erhaltenen Informationen ausschließlich zum internen Gebrauch bestimmt sind.'),
    (5, 26, 'Den Mitarbeitenden MUSS bewusst gemacht werden, die Informationssicherheit der Institution auchaußerhalb der Arbeitszeit und außerhalb des Betriebsgeländes zu schützen.'),
    (1, 27, 'Mitarbeitende MÜSSEN regelmäßig geschult bzw. weitergebildet werden.'),
    (2, 27, 'In allen Bereichen MUSSsichergestellt werden, dass kein Mitarbeitende mit veralteten Wissensstand arbeitet.'),
    (3, 27, 'Weiterhin SOLLTEden Mitarbeitenden während ihrer Beschäftigung die Möglichkeit gegeben werden, sich im Rahmenihres Tätigkeitsfeldes weiterzubilden.'),
    (4, 27, 'Werden Stellen besetzt, MÜSSEN die erforderlichen Qualifikationen und Fähigkeiten genau formuliertsein.'),
    (5, 27, 'Anschließend SOLLTE geprüft werden, ob diese bei den Bewerbenden für die Stelle tatsächlichvorhanden sind.'),
    (6, 27, 'Es MUSS sichergestellt sein, dass Stellen nur von Mitarbeitenden besetzt werden, fürdie sie qualifiziert sind.'),
    (1, 28, 'Neue Mitarbeitende SOLLTEN auf ihre Vertrauenswürdigkeit hin überprüft werden, bevor sieeingestellt werden.'),
    (2, 28, 'Soweit möglich, SOLLTEN alle an der Personalauswahl Beteiligten kontrollieren, ob die Angaben der Bewerbenden, die relevant für die Einschätzung ihrer Vertrauenswürdigkeit sind,glaubhaft sind.'),
    (3, 28, 'Insbesondere SOLLTE sorgfältig geprüft werden, ob der vorgelegte Lebenslauf korrekt,plausibel und vollständig ist.'),
    (4, 28, 'Dabei SOLLTEN auffällig erscheinende Angaben überprüft werden.'),
    (1, 29, 'Im Hochsicherheitsbereich SOLLTE eine zusätzliche Sicherheitsüberprüfung zusätzlich zurgrundlegenden Überprüfung der Vertrauenswürdigkeit von Mitarbeitenden durchgeführt werden.'),
    (2, 29, 'Arbeiten Mitarbeitende mit nach dem Geheimschutz klassifizierten Verschlusssachen, SOLLTEN sich die entsprechenden Mitarbeitenden einer Sicherheitsüberprüfung nach dem Sicherheitsüberprüfungsgesetz (SÜG) unterziehen.'),
    (3, 29, 'Diesbezüglich SOLLTE der oder dieInformationssicherheitsbeauftragte den Geheimschutzbeauftragten oder die Geheimschutzbeauftragte bzw. Sicherheitsbevollmächtigten oder Sicherheitsbevollmächtigte der Institution einbeziehen.'),
    (1, 30, 'Innerhalb einer Institution MÜSSEN alle relevanten Aufgaben und Funktionen klar definiert undvoneinander abgegrenzt sein.'),
    (2, 30, 'Es MÜSSEN verbindliche Regelungen für die Informationssicherheit fürdie verschiedenen betrieblichen Aspekte übergreifend festgelegt werden.'),
    (3, 30, 'Die Organisationsstrukturensowie verbindliche Regelungen MÜSSEN anlassbezogen überarbeitet werden.'),
    (4, 30, 'Die ÄnderungenMÜSSEN allen Mitarbeitenden bekannt gegeben werden.'),
    (1, 31, 'Für alle Geschäftsprozesse, Anwendungen, IT-Systeme, Räume und Gebäude sowieKommunikationsverbindungen MUSS festgelegt werden, wer für diese und deren Sicherheit zuständigist.'),
    (2, 31, 'Alle Mitarbeitenden MÜSSEN darüber informiert sein, insbesondere wofür sie zuständig sind undwelche damit verbundenen Aufgaben sie wahrnehmen.'),
    (1, 32, 'Institutionsfremde Personen MÜSSEN von Mitarbeitenden zu den Räumen begleitet werden.'),
    (2, 32, 'DieMitarbeitenden der Institution MÜSSEN institutionsfremde Personen in sensiblen Bereichenbeaufsichtigen.'),
    (3, 32, 'Die Mitarbeitenden SOLLTEN dazu angehalten werden, institutionsfremde Personen inden Räumen der Institution nicht unbeaufsichtigt zu lassen.'),
    (1, 33, 'Die Aufgaben und die hierfür erforderlichen Rollen und Funktionen MÜSSEN so strukturiert sein, dassunvereinbare Aufgaben wie operative und kontrollierende Funktionen auf verschiedene Personenverteilt werden.'),
    (2, 33, 'Für unvereinbare Funktionen MUSS eine Funktionstrennung festgelegt unddokumentiert sein.'),
    (3, 33, 'Auch Vertreter MÜSSEN der Funktionstrennung unterliegen.'),
    (1, 34, 'In jeder Institution MUSS es Ansprechpersonen für Sicherheitsfragen geben, die sowohl scheinbareinfache wie auch komplexe oder technische Fragen beantworten können.'),
    (2, 34, 'Die AnsprechpersonenMÜSSEN allen Mitarbeitenden der Institution bekannt sein.'),
    (3, 34, 'Diesbezügliche Informationen MÜSSEN inder Institution für alle verfügbar und leicht zugänglich sein.'),
    (1, 35, 'Alle Geräte und Betriebsmittel, die Einfluss auf die Informationssicherheit haben und die zurAufgabenerfüllung und zur Einhaltung der Sicherheitsanforderungen erforderlich sind, SOLLTEN inausreichender Menge vorhanden sein.'),
    (2, 35, 'Es SOLLTE geeignete Prüf- und Genehmigungsverfahren vorEinsatz der Geräte und Betriebsmittel geben.'),
    (3, 35, 'Geräte und Betriebsmittel SOLLTEN in geeignetenBestandsverzeichnissen aufgelistet werden.'),
    (4, 35, 'Um den Missbrauch von Daten zu verhindern, SOLLTE diezuverlässige Löschung oder Vernichtung von Geräten und Betriebsmitteln geregelt sein.'),
    (1, 36, 'Vor einem Umzug SOLLTEN frühzeitig Sicherheitsrichtlinien erarbeitet bzw. aktualisiert werden.'),
    (2, 36, 'AlleMitarbeitenden SOLLTEN über die vor, während und nach dem Umzug relevantenSicherheitsmaßnahmen informiert werden.'),
    (3, 36, 'Nach dem Umzug SOLLTE überprüft werden, ob dastransportierte Umzugsgut vollständig und unbeschädigt bzw. unverändert angekommen ist.'),
    (1, 37, 'Es SOLLTE eine Richtlinie erstellt werden, in der für alle Mitarbeitenden transparent beschrieben wird,welche Rahmenbedingungen bei der IT-Nutzung eingehalten werden müssen und welcheSicherheitsmaßnahmen zu ergreifen sind.'),
    (2, 37, 'Die Richtlinie SOLLTE folgende Punkte abdecken: Sicherheitsziele der Institution, wichtige Begriffe, Aufgaben und Rollen mit Bezug zur Informationssicherheit, Ansprechperson zu Fragen der Informationssicherheit sowie von den Mitarbeitenden umzusetzende und einzuhaltende Sicherheitsmaßnahmen.'),
    (3, 37, 'Die Richtlinie SOLLTE allen Benutzenden zur Kenntnis gegeben werden.'),
    (4, 37, 'Jeder neue BenutzendeSOLLTE die Kenntnisnahme und Beachtung der Richtlinie schriftlich bestätigen, bevor er dieInformationstechnik nutzen darf.'),
    (5, 37, 'Benutzende SOLLTEN die Richtlinie regelmäßig oder nach größerenÄnderungen erneut bestätigen.'),
    (6, 37, 'Die Richtlinie sollte zum Nachlesen für alle Mitarbeitenden freizugänglich abgelegt werden, beispielsweise im Intranet.'),
    (1, 38, 'Mobiltelefone SOLLTEN NICHT zu vertraulichen Besprechungen und Gesprächen mitgeführt werden. Falls erforderlich, SOLLTE dies durch Mobilfunk-Detektoren überprüft werden.'),
    (1, 40, 'Es MUSS klar geregelt werden, was Mitarbeitende bei der mobilen Nutzung von Laptopsberücksichtigen müssen.'),
